import java.util.*;

public class RandomizedDataStructure {

    private List<Integer> list; // Store elements in a list for O(1) access by index
    private Map<Integer, Integer> map; // Store element-index mapping for O(1) deletion

    /**
     * Initializes the RandomizedDataStructure.
     */
    public RandomizedDataStructure() {
        list = new ArrayList<>();
        map = new HashMap<>();
    }

    /**
     * Inserts an element into the data structure.
     *
     * @param val The element to insert.
     * @return true if the element was not already present, false otherwise.
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public boolean insert(int val) {
        if (map.containsKey(val)) {
            return false; // Element already exists
        }
        list.add(val); // Add to the end of the list
        map.put(val, list.size() - 1); // Store the index of the element
        return true;
    }

    /**
     * Deletes an element from the data structure, if it exists.
     *
     * @param val The element to delete.
     * @return true if the element was present and deleted, false otherwise.
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public boolean delete(int val) {
        if (!map.containsKey(val)) {
            return false; // Element does not exist
        }

        int indexToDelete = map.get(val);
        // If the element to delete is not the last element in the list,
        // swap it with the last element, then remove the last element.
        if (indexToDelete < list.size() - 1) {
            int lastElement = list.get(list.size() - 1);
            list.set(indexToDelete, lastElement); // Move last element to the position of deleted element
            map.put(lastElement, indexToDelete);   // Update the index of the last element
        }

        list.remove(list.size() - 1);          // Remove the last element
        map.remove(val);                       // Remove the element from the map
        return true;
    }

    /**
     * Returns a random element from the data structure.
     *
     * @return A random element.
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public int getRandom() {
        Random random = new Random();
        int randomIndex = random.nextInt(list.size()); // Generate a random index
        return list.get(randomIndex);                   // Return the element at that index
    }

