public class DriverPayoutSystem {

    /**
     * Represents a driver in the system.
     */
    public static class Driver {
        private String driverId;
        private BigDecimal totalEarnings;

        public Driver(String driverId, BigDecimal totalEarnings) {
            this.driverId = driverId;
            this.totalEarnings = totalEarnings;
        }

        public String getDriverId() {
            return driverId;
        }

        public BigDecimal getTotalEarnings() {
            return totalEarnings;
        }

        public void setTotalEarnings(BigDecimal totalEarnings) {
            this.totalEarnings = totalEarnings;
        }
    }

    /**
     * Interface for different types of offers that can be applied to a payout.
     */
    public interface Offer {
        BigDecimal apply(BigDecimal amount); // Applies the offer to a given amount
        String getDescription(); // Returns description of the offer. Useful for logging/debugging
    }

    /**
     * Concrete implementation of an offer that provides a flat bonus.
     */
    public static class FlatBonusOffer implements Offer {
        private BigDecimal bonusAmount;
        private String description;

        public FlatBonusOffer(BigDecimal bonusAmount, String description) {
            this.bonusAmount = bonusAmount;
            this.description = description;
        }

        @Override
        public BigDecimal apply(BigDecimal amount) {
            return amount.add(bonusAmount);
        }

        @Override
        public String getDescription() {
            return this.description;
        }
    }

    /**
     * Concrete implementation of an offer that applies a percentage discount.
     */
    public static class PercentageDiscountOffer implements Offer {
        private BigDecimal discountPercentage;
        private String description;

        public PercentageDiscountOffer(BigDecimal discountPercentage, String description) {
            this.discountPercentage = discountPercentage;
            this.description = description;
        }

        @Override
        public BigDecimal apply(BigDecimal amount) {
            BigDecimal discount = amount.multiply(discountPercentage.divide(new BigDecimal("100"), 2, RoundingMode.HALF_UP));
            return amount.subtract(discount);
        }

        @Override
        public String getDescription() {
            return this.description;
        }
    }

    /**
     * Calculates the payout amount for a driver after applying a list of offers.
     *
     * @param driver  The driver for whom to calculate the payout.
     * @param offers  The list of offers to apply to the payout.
     * @return The final payout amount after applying all offers.
     *
     * Time Complexity: O(n), where n is the number of offers.
     * Space Complexity: O(1)
     */
    public BigDecimal calculatePayout(Driver driver, List<Offer> offers) {
        BigDecimal payoutAmount = driver.getTotalEarnings();
        if (offers != null && !offers.isEmpty()) {
            for (Offer offer : offers) {
                payoutAmount = offer.apply(payoutAmount);
            }
        }
        return payoutAmount.setScale(2, RoundingMode.HALF_UP); // Round to 2 decimal places
    }

