import java.util.*;

public class GroupAnagrams {

    /**
     * Groups anagrams from an array of strings.
     *
     * @param strs The input array of strings.
     * @return A list of lists, where each inner list contains anagrams.
     *
     * Time Complexity: O(N * K log K), where N is the number of strings in the input array
     * and K is the average length of the strings.  We iterate through each string (N times),
     * and for each string, we sort it (K log K time).
     * Space Complexity: O(N * K), where N is the number of strings and K is the average length of strings.
     * In the worst case, all strings are unique (no anagrams), so we store N strings of average length K in the map.
     */
    public List<List<String>> groupAnagrams(String[] strs) {
        if (strs == null || strs.length == 0) {
            return new ArrayList<>(); // Handle empty or null input
        }

        Map<String, List<String>> anagramGroups = new HashMap<>();

        for (String str : strs) {
            // Sort the characters of the string to create a canonical form (key)
            char[] charArray = str.toCharArray();
            Arrays.sort(charArray);
            String sortedString = new String(charArray);

            // Add the string to the corresponding anagram group
            if (!anagramGroups.containsKey(sortedString)) {
                anagramGroups.put(sortedString, new ArrayList<>());
            }
            anagramGroups.get(sortedString).add(str);
        }

        // Return the list of anagram groups
        return new ArrayList<>(anagramGroups.values());
    }

    public static void main(String[] args) {
        GroupAnagrams groupAnagrams = new GroupAnagrams();

        // Test case 1: Basic test case
        String[] strs1 = {"eat", "tea", "tan", "ate", "nat", "bat"};
        List<List<String>> result1 = groupAnagrams.groupAnagrams(strs1);
        System.out.println("Test Case 1: " + result1);
        // Expected output (order may vary): [["eat","tea","ate"],["tan","nat"],["bat"]]

        // Test case 2: Empty input array
        String[] strs2 = {};
        List<List<String>> result2 = groupAnagrams.groupAnagrams(strs2);
        System.out.println("Test Case 2: " + result2);
        // Expected output: []

        // Test case 3: Single word input
        String[] strs3 = {"abc"};
        List<List<String>> result3 = groupAnagrams.groupAnagrams(strs3);
        System.out.println("Test Case 3: " + result3);
        // Expected output: [["abc"]]

        // Test case 4: All anagrams
        String[] strs4 = {"eat", "tea", "ate"};
        List<List<String>> result4 = groupAnagrams.groupAnagrams(strs4);
        System.out.println("Test Case 4: " + result4);
        // Expected output (order may vary): [["eat","tea","ate"]]

        // Test case 5: Input with duplicates
        String[] strs5 = {"eat", "tea", "ate", "eat"};
        List<List<String>> result5 = groupAnagrams.groupAnagrams(strs5);
        System.out.println("Test Case 5: " + result5);
        //Expected Output (order may vary): [["eat","tea","ate","eat"]]

        // Test case 6: Different length strings
