import java.util.List;
import java.util.ArrayList;

/**
 * Solution for finding the maximum product that can be obtained by multiplying
 * elements from any subset of an array of positive integers.
 */
public class MaxProductSubset {

    /**
     * Calculates the maximum product of a subset of the given list of positive
     * integers.
     *
     * @param nums The list of positive integers.
     * @return The maximum product that can be obtained from a subset of the array.
     *         Returns 1 if the input list is empty.
     */
    public long maxMultiple(List<Integer> nums) {
        if (nums == null || nums.isEmpty()) {
            return 1; // Product of an empty subset is 1.
        }

        long product = 1;
        for (int num : nums) {
            product *= num;
        }

        return product;
    }

    /**
     * Main method for testing the maxMultiple function.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        MaxProductSubset solution = new MaxProductSubset();

        // Test case 1
        List<Integer> nums1 = List.of(2, 3, 4, 5);
        System.out.println("Input: " + nums1);
        System.out.println("Output: " + solution.maxMultiple(nums1)); // Expected: 120

        // Test case 2
        List<Integer> nums2 = List.of(1, 2, 3, 4, 5);
        System.out.println("Input: " + nums2);
        System.out.println("Output: " + solution.maxMultiple(nums2)); // Expected: 120

        // Test case 3 - With duplicates
        List<Integer> nums3 = List.of(2, 2, 3, 3);
        System.out.println("Input: " + nums3);
        System.out.println("Output: " + solution.maxMultiple(nums3)); // Expected: 36

        // Test case 4 - Single element
        List<Integer> nums4 = List.of(5);
        System.out.println("Input: " + nums4);
        System.out.println("Output: " + solution.maxMultiple(nums4)); // Expected: 5

        // Test case 5 - Empty list (edge case)
        List<Integer> nums5 = new ArrayList<>();
        System.out.println("Input: " + nums5);
        System.out.println("Output: " + solution.maxMultiple(nums5)); // Expected: 1

        // Test case 6 - Larger numbers to test for potential overflow
        List<Integer> nums6 = List.of(10, 10, 10);
        System.out.println("Input: " + nums6);
        System.out.println("Output: " + solution.maxMultiple(nums6)); // Expected: 1000

        // Test case 7 - Mixture of smaller and larger numbers
        List<Integer> nums7 = List.of(1, 5, 10, 2);
        System.out.println("Input: " + nums7);
        System.out.println("Output: " + solution.maxMultiple(nums7)); // Expected: 100

    }
}

/**
 * Time and Space Complexity Analysis:
 *
 * Time Complexity: O(n), where n is the number of elements in the input list.
 *                  The code iterates through the list once to calculate the product.
 *
 * Space Complexity: O(1).  The code uses a constant amount of extra space, regardless of the input size.
 */
