import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.security.SecureRandom;
import java.time.Instant;
import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.locks.ReadWriteLock;
import java.util.concurrent.locks.ReentrantReadWriteLock;
import java.util.stream.Collectors;

public class CarWaleNewsSystem {

    // In-memory data structures for demonstration purposes.  In a real system,
    // these would be replaced with a database like PostgreSQL or Cassandra.
    private final Map<String, User> users = new ConcurrentHashMap<>(); // userId -> User
    private final Map<String, NewsArticle> articles = new ConcurrentHashMap<>(); // articleId -> NewsArticle
    private final Map<String, List<String>> userNewsFeed = new ConcurrentHashMap<>(); // userId -> List<articleId>
    private final Map<String, List<String>> articleComments = new ConcurrentHashMap<>(); // articleId -> List<CommentId>
    private final Map<String, Comment> comments = new ConcurrentHashMap<>(); // commentId -> Comment
    private final Map<String, List<String>> authorArticles = new ConcurrentHashMap<>(); // authorId -> List<articleId>
    private final Map<String, List<String>> tagArticles = new ConcurrentHashMap<>(); // tag -> List<articleId>

    // Locks for concurrent access to data structures.  Using ReadWriteLocks
    // to allow multiple readers or a single writer.
    private final ReadWriteLock userLock = new ReentrantReadWriteLock();
    private final ReadWriteLock articleLock = new ReentrantReadWriteLock();
    private final ReadWriteLock feedLock = new ReentrantReadWriteLock();
    private final ReadWriteLock commentLock = new ReentrantReadWriteLock();
    private final ReadWriteLock authorArticleLock = new ReentrantReadWriteLock();
    private final ReadWriteLock tagArticleLock = new ReentrantReadWriteLock();


    // Helper class definitions
    public static class User {
        private String userId;
        private String username;
        private String passwordHash;
        private String salt;
        private Set<String> preferences; // article tags or keywords

        public User(String username, String password, Set<String> preferences) {
            this.userId = UUID.randomUUID().toString(); // unique user ID
            this.username = username;
            this.salt = generateSalt();
            this.passwordHash = hashPassword(password, this.salt);
            this.preferences = preferences;
        }

        public String getUserId() {
            return userId;
        }

        public String getUsername() {
            return username;
        }

        public String getPasswordHash() {
            return passwordHash;
        }

        public String getSalt() {
            return salt;
        }

        public Set<String> getPreferences() {
            return preferences;
        }

        public void setPreferences(Set<String> preferences) {
            this.preferences = preferences;
        }
    }

    public static class NewsArticle {
        private String articleId;
        private String title;
        private String content;
        private String authorId;
        private Instant publicationDate;
        private Set<String> tags;

        public NewsArticle(String title, String content, String authorId, Set<String> tags) {
            this.articleId = UUID.randomUUID().toString();
            this.title = title;
            this.content = content;
            this.authorId = authorId;
            this.publicationDate = Instant.now(); // Current time of publication
            this.tags = tags;
        }

        public String getArticleId() {
            return articleId;
        }

        public String getTitle() {
            return title;
        }

        public String getContent() {
            return content;
        }

        public String getAuthorId() {
            return authorId;
        }

        public Instant getPublicationDate() {
            return publicationDate;
        }

        public Set<String> getTags() {
            return tags;
        }

        public void setTags(Set<String> tags) {
            this.tags = tags;
        }

        public void setContent(String content) {
            this.content = content;
        }

        public void setTitle(String title) {
            this.title = title;
        }
    }

    public static class Comment {
        private String commentId;
        private String articleId;
        private String userId;
        private String text;
        private Instant timestamp;

        public Comment(String articleId, String userId, String text) {
            this.commentId = UUID.randomUUID().toString();
            this.articleId = articleId;
            this.userId = userId;
            this.text = text;
            this.timestamp = Instant.now();
        }

        public String getCommentId() {
            return commentId;
        }

        public String getArticleId() {
            return articleId;
        }

        public String getUserId() {
            return userId;
        }

        public String getText() {
            return text;
        }

        public Instant getTimestamp() {
            return timestamp;
        }
    }

    // User Management Methods

    /**
     * Registers a new user.
     *
     * Time Complexity: O(1) - Assuming hash functions are constant time.
     * Space Complexity: O(1)
     *
     * @param username  The desired username.
     * @param password  The user's password.
     * @param preferences The user's preferences
     * @return The new user object, or null if registration fails (e.g., username already exists).
     */
    public User registerUser(String username, String password, Set<String> preferences) {
        userLock.writeLock().lock();
        try {
            if (users.containsKey(username)) {
                return null; // Username already exists
            }
            User newUser = new User(username, password, preferences);
            users.put(newUser.getUsername(), newUser);
            return newUser;
        } finally {
            userLock.writeLock().unlock();
        }
    }


    /**
     * Logs in a user.
     *
     * Time Complexity: O(1) - Assuming hash functions are constant time.
     * Space Complexity: O(1)
     *
     * @param username The username.
     * @param password The password.
     * @return The User object if login is successful, null otherwise.
     */
    public User loginUser(String username, String password) {
        userLock.readLock().lock();
        try {
            User user = users.get(username);
            if (user != null && verifyPassword(password, user.getPasswordHash(), user.getSalt())) {
                return user;
            }
            return null; // Login failed
        } finally {
            userLock.readLock().unlock();
        }
    }

    /**
     * Updates user preferences.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @param userId The ID of the user to update.
     * @param preferences The new set of preferences for the user.
     * @return True if the update was successful, false otherwise.
     */
    public boolean updateUserPreferences(String userId, Set<String> preferences) {
        userLock.writeLock().lock();
        try {
            User user = users.values().stream().filter(u -> u.getUserId().equals(userId)).findFirst().orElse(null);
            if (user != null) {
                user.setPreferences(preferences);
                // Update the user object in the map directly to reflect changes
                users.put(user.getUsername(), user);
                return true;
            }
            return false; // User not found
        } finally {
            userLock.writeLock().unlock();
        }
    }

    // News Article Management Methods

    /**
     * Creates a new news article.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @param title    The title of the article.
     * @param content  The content of the article.
     * @param authorId The ID of the author.
     * @param tags     The set of tags associated with the article.
     * @return The new NewsArticle object.
     */
    public NewsArticle createArticle(String title, String content, String authorId, Set<String> tags) {
        articleLock.writeLock().lock();
        authorArticleLock.writeLock().lock();
        tagArticleLock.writeLock().lock();
        try {
            NewsArticle newArticle = new NewsArticle(title, content, authorId, tags);
            articles.put(newArticle.getArticleId(), newArticle);

            //Update authorArticles map to link author to article
            authorArticles.computeIfAbsent(authorId, k -> new ArrayList<>()).add(newArticle.getArticleId());

            //Update tagArticles map to link tag to article
            tags.forEach(tag -> tagArticles.computeIfAbsent(tag, k -> new ArrayList<>()).add(newArticle.getArticleId()));

            return newArticle;
        } finally {
            articleLock.writeLock().unlock();
            authorArticleLock.writeLock().unlock();
            tagArticleLock.writeLock().unlock();
        }
    }

    /**
     * Edits an existing news article.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @param articleId The ID of the article to edit.
     * @param newTitle  The new title for the article.
     * @param newContent The new content for the article.
     * @param newTags The new tags for the article.
     * @return True if the edit was successful, false otherwise.
     */
    public boolean editArticle(String articleId, String newTitle, String newContent, Set<String> newTags) {
        articleLock.writeLock().lock();
        tagArticleLock.writeLock().lock();
        try {
            NewsArticle article = articles.get(articleId);
            if (article != null) {
                Set<String> oldTags = article.getTags();

                // Remove old tags from tagArticles map
                oldTags.forEach(tag -> {
                    tagArticleLock.writeLock().lock();
                    try {
                        if (tagArticles.containsKey(tag)) {
                            tagArticles.get(tag).remove(articleId);
                            if (tagArticles.get(tag).isEmpty()) {
                                tagArticles.remove(tag);
                            }
                        }
                    } finally {
                        tagArticleLock.writeLock().unlock();
                    }
                });

                // Add new tags to tagArticles map
                newTags.forEach(tag -> {
                    tagArticleLock.writeLock().lock();
                    try {
                        tagArticles.computeIfAbsent(tag, k -> new ArrayList<>()).add(articleId);
                    } finally {
                        tagArticleLock.writeLock().unlock();
                    }
                });

                article.setTitle(newTitle);
                article.setContent(newContent);
                article.setTags(newTags);
                return true;
            }
            return false; // Article not found
        } finally {
            articleLock.writeLock().unlock();
            tagArticleLock.writeLock().unlock();
        }
    }


    /**
     * Retrieves an article by its ID.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @param articleId The ID of the article.
     * @return The NewsArticle object, or null if not found.
     */
    public NewsArticle getArticle(String articleId) {
        articleLock.readLock().lock();
        try {
            return articles.get(articleId);
        } finally {
            articleLock.readLock().unlock();
        }
    }

    /**
     * Searches for articles based on keywords.
     * This is a basic search and can be improved with more sophisticated indexing.
     *
     * Time Complexity: O(N * M) where N is the number of articles and M is the average length of title + content.
     * Space Complexity: O(K) where K is the number of results.
     *
     * @param keyword The keyword to search for.
     * @return A list of NewsArticle objects that match the keyword.
     */
    public List<NewsArticle> searchArticles(String keyword) {
        articleLock.readLock().lock();
        try {
            return articles.values().stream()
                    .filter(article -> article.getTitle().toLowerCase().contains(keyword.toLowerCase()) ||
                            article.getContent().toLowerCase().contains(keyword.toLowerCase()))
                    .collect(Collectors.toList());
        } finally {
            articleLock.readLock().unlock();
        }
    }

    /**
     * Searches for articles based on tag.
     *
     * Time Complexity: O(1) (Average case) - Assumes the tagArticles map has O(1) lookup time.
     * Space Complexity: O(K) - Where K is the number of article IDs associated with the given tag.
     *
     * @param tag The tag to search for.
     * @return A list of NewsArticle objects that match the tag.
     */
    public List<NewsArticle> searchArticlesByTag(String tag) {
        tagArticleLock.readLock().lock();
        articleLock.readLock().lock();
        try {
            List<String> articleIds = tagArticles.getOrDefault(tag, Collections.emptyList());
            return articleIds.stream()
                    .map(articles::get)
                    .filter(Objects::nonNull) //Handle the case that some articles may have been deleted
                    .collect(Collectors.toList());
        } finally {
            tagArticleLock.readLock().unlock();
            articleLock.readLock().unlock();
        }
    }

    // News Feed Methods

    /**
     * Generates a personalized news feed for a user.
     *
     * Time Complexity: O(U * A) where U is the number of user preferences and A is number of articles.
     * Space Complexity: O(K) where K is number of articles in the feed.
     *
     * @param userId The ID of the user.
     * @return A list of NewsArticle objects for the user's news feed.
     */
    public List<NewsArticle> generateNewsFeed(String userId) {
        feedLock.writeLock().lock();
        userLock.readLock().lock();
        articleLock.readLock().lock();
        try {
            User user = users.values().stream().filter(u -> u.getUserId().equals(userId)).findFirst().orElse(null);
            if (user == null) {
                return Collections.emptyList(); // User not found
            }

            Set<String> userPreferences = user.getPreferences();

            //Basic news feed generation based on matching tags with preferences.
            List<NewsArticle> feed = articles.values().stream()
                    .filter(article -> article.getTags().stream().anyMatch(userPreferences::contains))
                    .sorted(Comparator.comparing(NewsArticle::getPublicationDate).reversed()) // Sort by date descending
                    .collect(Collectors.toList());

            //Store the news feed
            List<String> articleIds = feed.stream().map(NewsArticle::getArticleId).collect(Collectors.toList());
            userNewsFeed.put(userId, articleIds);

            return feed;

        } finally {
            feedLock.writeLock().unlock();
            userLock.readLock().unlock();
            articleLock.readLock().unlock();
        }
    }


    /**
     * Retrieves the user's previously generated news feed.
     * This provides caching functionality, and simulates a read operation.
     *
     * Time Complexity: O(N) where N is the size of cached feed, due to mapping article IDs to article objects.
     * Space Complexity: O(N) - Where N is the size of the user's news feed
     *
     * @param userId The ID of the user.
     * @return A list of NewsArticle objects from the user's news feed, or an empty list if the feed has not been generated.
     */
    public List<NewsArticle> getCachedNewsFeed(String userId) {
        feedLock.readLock().lock();
        articleLock.readLock().lock();
        try {
            List<String> articleIds = userNewsFeed.getOrDefault(userId, Collections.emptyList());
            return articleIds.stream()
                    .map(articles::get)
                    .filter(Objects::nonNull)
                    .collect(Collectors.toList());
        } finally {
            feedLock.readLock().unlock();
            articleLock.readLock().unlock();
        }
    }

    // Social Features Methods

    /**
     * Adds a comment to a news article.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @param articleId The ID of the article.
     * @param userId    The ID of the user posting the comment.
     * @param text      The comment text.
     * @return The newly created Comment object.
     */
    public Comment addComment(String articleId, String userId, String text) {
        commentLock.writeLock().lock();
        try {
            Comment newComment = new Comment(articleId, userId, text);
            comments.put(newComment.getCommentId(), newComment);
            articleComments.computeIfAbsent(articleId, k -> new ArrayList<>()).add(newComment.getCommentId());
            return newComment;
        } finally {
            commentLock.writeLock().unlock();
        }
    }

    /**
     * Retrieves comments for an article.
     *
     * Time Complexity: O(N) - Where N is number of comments
     * Space Complexity: O(N) - Where N is the number of comments for the article
     *
     * @param articleId The ID of the article.
     * @return A list of Comment objects for the article.
     */
    public List<Comment> getCommentsForArticle(String articleId) {
        commentLock.readLock().lock();
        try {
            List<String> commentIds = articleComments.getOrDefault(articleId, Collections.emptyList());
            return commentIds.stream()
                    .map(comments::get)
                    .filter(Objects::nonNull)
                    .collect(Collectors.toList());
        } finally {
            commentLock.readLock().unlock();
        }
    }

    /**
     * Get articles written by an author.
     *
     * Time Complexity: O(N) where N is the number of articles written by the author
     * Space Complexity: O(N) where N is the number of articles written by the author
     *
     * @param authorId The ID of the author.
     * @return A list of NewsArticle objects written by the author.
     */
    public List<NewsArticle> getArticlesByAuthor(String authorId) {
        authorArticleLock.readLock().lock();
        articleLock.readLock().lock();
        try {
            List<String> articleIds = authorArticles.getOrDefault(authorId, Collections.emptyList());
            return articleIds.stream()
                    .map(articles::get)
                    .filter(Objects::nonNull)
                    .collect(Collectors.toList());
        } finally {
            authorArticleLock.readLock().unlock();
            articleLock.readLock().unlock();
        }
    }


    // Security methods

    /**
     * Generates a random salt for password hashing.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @return The generated salt.
     */
    private String generateSalt() {
        SecureRandom random = new SecureRandom();
        byte[] salt = new byte[16];
        random.nextBytes(salt);
        return Base64.getEncoder().encodeToString(salt);
    }

    /**
     * Hashes a password using SHA-256 with a salt.
     *
     * Time Complexity: O(1) - Hashing function depends on implementation details of SHA-256
     * Space Complexity: O(1)
     *
     * @param password The password to hash.
     * @param salt     The salt to use.
     * @return The hashed password.
     */
    private String hashPassword(String password, String salt) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            String saltedPassword = password + salt;
            byte[] hashedPassword = md.digest(saltedPassword.getBytes());
            return Base64.getEncoder().encodeToString(hashedPassword);
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException("SHA-256 algorithm not available.", e);
        }
    }

    /**
     * Verifies a password against a stored hash and salt.
     *
     * Time Complexity: O(1) - Hashing function depends on implementation details of SHA-256
     * Space Complexity: O(1)
     *
     * @param password The password to verify.
     * @param hashedPassword The stored hash.
     * @param salt The salt used to generate the hash.
     * @return True if the password is correct, false otherwise.
     */
    private boolean verifyPassword(String password, String hashedPassword, String salt) {
        String newHash = hashPassword(password, salt);
        return newHash.equals(hashedPassword);
    }


