import java.util.HashMap;
import java.util.LinkedList;
import java.util.Map;

public class LRUCache {

    private final int capacity;
    private final Map<Integer, Integer> cache;
    private final LinkedList<Integer> lruQueue; // Doubly Linked List can also be used for O(1) remove in the middle

    public LRUCache(int capacity) {
        this.capacity = capacity;
        this.cache = new HashMap<>(capacity);
        this.lruQueue = new LinkedList<>();
    }

    /**
     * Retrieves the value associated with a given key from the cache.
     * If the key is present, it updates the LRU queue and returns the value.
     * Otherwise, it returns -1.
     *
     * Time Complexity: O(1) - amortized due to hashmap lookup and queue operations.
     * Space Complexity: O(1) - constant operations.
     *
     * @param key The key to retrieve.
     * @return The value associated with the key, or -1 if the key is not present.
     */
    public int get(int key) {
        if (cache.containsKey(key)) {
            // Update LRU queue
            lruQueue.remove(Integer.valueOf(key)); // Remove the existing key
            lruQueue.addFirst(key);                // Add the key to the front of the queue

            return cache.get(key);
        }
        return -1;
    }

    /**
     * Inserts a key-value pair into the cache.
     * If the key is already present, it updates the value and the LRU queue.
     * If the cache is full, it evicts the least recently used key before inserting the new pair.
     *
     * Time Complexity: O(1) - amortized due to hashmap operations and queue operations.
     * Space Complexity: O(1) - constant operations.
     *
     * @param key   The key to insert.
     * @param value The value associated with the key.
     */
    public void put(int key, int value) {
        if (cache.containsKey(key)) {
            // Update value and LRU queue
            cache.put(key, value);
            lruQueue.remove(Integer.valueOf(key)); // Remove the existing key
            lruQueue.addFirst(key);                // Add the key to the front of the queue
        } else {
            if (cache.size() == capacity) {
                // Evict the least recently used key
                int lruKey = lruQueue.removeLast();
                cache.remove(lruKey);
            }
            // Insert the new key-value pair
            cache.put(key, value);
            lruQueue.addFirst(key);
        }
    }

    public static void main(String[] args) {
        // Test case 1: Basic operations
        LRUCache cache1 = new LRUCache(2);
        cache1.put(1, 1);
        cache1.put(2, 2);
        System.out.println(cache1.get(1)); // returns 1
        cache1.put(3, 3);                 // evicts key 2
        System.out.println(cache1.get(2)); // returns -1 (not found)
        cache1.put(4, 4);                 // evicts key 1
        System.out.println(cache1.get(1)); // returns -1 (not found)
        System.out.println(cache1.get(3)); // returns 3
        System.out.println(cache1.get(4)); // returns 4

        // Test case 2: Capacity of 1
        LRUCache cache2 = new LRUCache(1);
        cache2.put(1, 1);
        System.out.println(cache2.get(1)); // returns 1
        cache2.put(2, 2);
        System.out.println(cache2.get(1)); // returns -1
        System.out.println(cache2.get(2)); // returns 2

        // Test case 3: Update existing key
        LRUCache cache3 = new LRUCache(2);
        cache3.put(1, 1);
        cache3.put(2, 2);
        cache3.put(1, 3); // Update key 1
        System.out.println(cache3.get(1)); // returns 3
        System.out.println(cache3.get(2)); // returns 2

        // Test case 4: Inserting the same key multiple times will always update it and maintain the LRU order
        LRUCache cache4 = new LRUCache(2);
        cache4.put(1,1);
        cache4.put(2,2);
        cache4.put(1,1);
        cache4.put(3,3);
        System.out.println(cache4.get(1)); //returns 1
        System.out.println(cache4.get(2)); //returns -1

        // Test case 5: Empty cache
        LRUCache cache5 = new LRUCache(0);
        cache5.put(1,1);
        System.out.println(cache5.get(1)); //returns -1

        // Test case 6: Large number of operations to check time complexity
        LRUCache cache6 = new LRUCache(100);
        for (int i = 0; i < 200; i++) {
            cache6.put(i, i * 2);
            if (i % 2 == 0) {
                cache6.get(i);
            }
        }
        System.out.println("Large operation test complete.");


    }
}
