import java.util.Arrays;

public class LargestValidSequence {

    /**
     * Constructs the lexicographically largest valid sequence of length N.
     * A valid sequence is defined as a sequence of distinct positive integers where for every i-th index (1 ≤ i ≤ N), the following conditions hold:
     * 1. The number at i-th index is divisible by i.
     * 2. The number at i-th index is not divisible by any number from 2 to (i-1).
     *
     * @param n The length of the sequence.
     * @return The lexicographically largest valid sequence of length n.
     *
     * Time Complexity: O(N^2) - Dominates the execution time. Iterating through potential candidates and checking divisibility.
     * Space Complexity: O(N) - For storing the sequence and the used array.
     */
    public static int[] constructLargestSequence(int n) {
        int[] sequence = new int[n];
        boolean[] used = new boolean[n + 1]; // Track numbers already used. Index 0 is not used.

        for (int i = 0; i < n; i++) {
            // Start from the largest possible number and work down.
            for (int j = n; j >= 1; j--) {
                // Check if 'j' is a valid candidate for the current index.

                //Condition 1: Check if j is divisible by the index (i+1)
                if (j % (i + 1) != 0) {
                    continue; // Not divisible, try the next smaller number.
                }

                // Condition 2: Check if 'j' has already been used.
                if (used[j]) {
                    continue; // Already used, try the next smaller number.
                }

                boolean divisibleByPrevious = false;
                // Condition 3: Check if 'j' is divisible by any of the previously used numbers.
                for (int k = 1; k <= i; k++) {
                    if (sequence[k - 1] > 1 && j % sequence[k - 1] == 0) {
                        divisibleByPrevious = true;
                        break; // Divisible by a previous element, so move on
                    }
                }

                if (divisibleByPrevious) {
                    continue;
                }

                // If all conditions are met, assign 'j' to the current index.
                sequence[i] = j;
                used[j] = true;
                break; // Move to the next index.
            }
        }

        return sequence;
    }

    public static void main(String[] args) {
        // Test cases
        testCase(3);
        testCase(6);
        testCase(1);
        testCase(2);
        testCase(4);
        testCase(5);
        testCase(7);
        testCase(8);
        testCase(9);
        testCase(10);

    }

    private static void testCase(int n) {
        int[] result = constructLargestSequence(n);
        System.out.println("Input: N = " + n);
        System.out.println("Output: " + Arrays.toString(result));
        System.out.println("--------------------");
    }
}
