import java.util.Stack;

public class ValidParenthesisString {

    /**
     * Checks if a string containing '(', ')', and '*' is a valid parenthesis string.
     *
     * A string is valid if:
     * 1. Every left parenthesis '(' must have a corresponding right parenthesis ')' to its right.
     * 2. Every right parenthesis ')' must have a corresponding left parenthesis '(' to its left.
     * 3. The '*' character can represent either a left parenthesis '(', a right parenthesis ')', or an empty string.
     *
     * @param s The input string.
     * @return True if the string is valid, false otherwise.
     *
     * Time Complexity: O(n), where n is the length of the string.  We iterate through the string once.
     * Space Complexity: O(1). We use two integer variables (low and high) to keep track of the counts.
     */
    public boolean checkValidString(String s) {
        int low = 0;  // Minimum number of open parentheses needed
        int high = 0; // Maximum number of open parentheses possible

        for (char c : s.toCharArray()) {
            if (c == '(') {
                low++;
                high++;
            } else if (c == ')') {
                low--;
                high--;
            } else { // c == '*'
                low--; // Treat '*' as ')'
                high++; // Treat '*' as '('
            }

            low = Math.max(low, 0); // Ensure low doesn't become negative

            if (high < 0) {
                return false; // Too many closing parentheses
            }
        }

        return low == 0; // If low is 0, it means all '(' are matched
    }



    public static void main(String[] args) {
        ValidParenthesisString validator = new ValidParenthesisString();

        // Test cases
        String s1 = "()";
        System.out.println("Input: " + s1 + ", Output: " + validator.checkValidString(s1)); // Expected: true

        String s2 = "(*)";
        System.out.println("Input: " + s2 + ", Output: " + validator.checkValidString(s2)); // Expected: true

        String s3 = "(*))";
        System.out.println("Input: " + s3 + ", Output: " + validator.checkValidString(s3)); // Expected: true

        String s4 = "((*";
        System.out.println("Input: " + s4 + ", Output: " + validator.checkValidString(s4)); // Expected: true

        String s5 = "((())";
        System.out.println("Input: " + s5 + ", Output: " + validator.checkValidString(s5)); // Expected: false

        String s6 = "(*(()))";
        System.out.println("Input: " + s6 + ", Output: " + validator.checkValidString(s6)); // Expected: true

        String s7 = "((*)";
        System.out.println("Input: " + s7 + ", Output: " + validator.checkValidString(s7)); // Expected: true

        String s8 = "())";
        System.out.println("Input: " + s8 + ", Output: " + validator.checkValidString(s8)); // Expected: false

        String s9 = "";
        System.out.println("Input: " + s9 + ", Output: " + validator.checkValidString(s9)); // Expected: true

        String s10 = "*";
        System.out.println("Input: " + s10 + ", Output: " + validator.checkValidString(s10)); // Expected: true

        String s11 = ")(*";
        System.out.println("Input: " + s11 + ", Output: " + validator.checkValidString(s11)); // Expected: false

