import java.util.HashMap;
import java.util.Map;
import java.util.Stack;

public class ZumaGame {

    /**
     * Solves the Zuma game problem.
     *
     * @param board The string representing the initial state of the balls on the track.
     * @param hand  The string representing the balls available in the hand.
     * @return The minimum number of balls needed to insert to eliminate all balls, or -1 if impossible.
     */
    public int findMinStep(String board, String hand) {
        // Create a map to store the frequency of each color in the hand.
        Map<Character, Integer> handMap = new HashMap<>();
        for (char c : hand.toCharArray()) {
            handMap.put(c, handMap.getOrDefault(c, 0) + 1);
        }

        int minSteps = solve(board, handMap);
        return minSteps == Integer.MAX_VALUE ? -1 : minSteps;
    }

    /**
     * Recursive helper function to solve the Zuma game.
     *
     * @param board   The current state of the board.
     * @param handMap A map representing the remaining balls in the hand and their quantities.
     * @return The minimum number of steps required to clear the board, or Integer.MAX_VALUE if not possible.
     */
    private int solve(String board, Map<Character, Integer> handMap) {
        // Base case: Board is empty.
        if (board.isEmpty()) {
            return 0;
        }

        int minSteps = Integer.MAX_VALUE;

        // Iterate through each position in the board to consider inserting a ball.
        for (int i = 0; i < board.length(); i++) {
            char currentChar = board.charAt(i);

            // Check for all the colors in our hand that are present in the current state of the board
            if (handMap.containsKey(currentChar) && handMap.get(currentChar) > 0) {
                // Try inserting the ball at this position.
                // Count how many consecutive balls of the same color exist before the current index.
                int j = i + 1;
                while (j < board.length() && board.charAt(j) == currentChar) {
                    j++;
                }

                int requiredBalls = 3 - (j - i); // Required balls for this set to be completely removed
                // Try inserting balls as long as we have enough balls for this removal
                if (requiredBalls <= handMap.get(currentChar)) {
                    // Reduce count in the handMap
                    handMap.put(currentChar, handMap.get(currentChar) - requiredBalls);
                    String newBoard = reduce(board.substring(0, i) + board.substring(j));

                    // Recursively call solve with new board and hand
                    int steps = solve(newBoard, handMap);
                    if (steps != Integer.MAX_VALUE) {
                        minSteps = Math.min(minSteps, requiredBalls + steps);
                    }

                    // Backtrack by restoring the handMap
                    handMap.put(currentChar, handMap.get(currentChar) + requiredBalls);
                }

            }
        }

        return minSteps;
    }

    /**
     * Reduces the board by removing consecutive groups of three or more balls of the same color.
     *
     * @param board The board to reduce.
     * @return The reduced board.
     */
    private String reduce(String board) {
        Stack<Character> stack = new Stack<>();
        Stack<Integer> count = new Stack<>();

        for (char c : board.toCharArray()) {
            if (!stack.isEmpty() && stack.peek() == c) {
                count.push(count.pop() + 1);
            } else {
                stack.push(c);
                count.push(1);
            }

            if (count.peek() >= 3) {
                stack.pop();
                count.pop();
            }
        }

        StringBuilder sb = new StringBuilder();
        while (!stack.isEmpty()) {
            int num = count.pop();
            char ch = stack.pop();
            for (int i = 0; i < num; i++) {
                sb.append(ch);
            }
        }

        return sb.reverse().toString();
    }

    public static void main(String[] args) {
        ZumaGame zumaGame = new ZumaGame();

        // Test cases
        String board1 = "WRRBBWW";
        String hand1 = "RB";
        System.out.println("Test Case 1: board = " + board1 + ", hand = " + hand1 + ", result = " + zumaGame.findMinStep(board1, hand1)); // Expected: 2

        String board2 = "WWRRBBWW";
        String hand2 = "RB";
        System.out.println("Test Case 2: board = " + board2 + ", hand = " + hand2 + ", result = " + zumaGame.findMinStep(board2, hand2)); // Expected: -1

        String board3 = "G";
        String hand3 = "G";
        System.out.println("Test Case 3: board = " + board3 + ", hand = " + hand3 + ", result = " + zumaGame.findMinStep(board3, hand3)); // Expected: -1

        String board4 = "RRGGBB";
        String hand4 = "RGB";
        System.out.println("Test Case 4: board = " + board4 + ", hand = " + hand4 + ", result = " + zumaGame.findMinStep(board4, hand4)); // Expected: 3

        String board5 = "RRWWRRBBRR";
        String hand5 = "WB";
        System.out.println("Test Case 5: board = " + board5 + ", hand = " + hand5 + ", result = " + zumaGame.findMinStep(board5, hand5)); // Expected: 2

        String board6 = "WWGWGW";
        String hand6 = "GWB";
        System.out.println("Test Case 6: board = " + board6 + ", hand = " + hand6 + ", result = " + zumaGame.findMinStep(board6, hand6)); // Expected: -1

        String board7 = "W";
        String hand7 = "W";
        System.out.println("Test Case 7: board = " + board7 + ", hand = " + hand7 + ", result = " + zumaGame.findMinStep(board7, hand7)); //Expected -1

        String board8 = "BBBWWWWWB";
        String hand8 = "BB";
        System.out.println("Test Case 8: board = " + board8 + ", hand = " + hand8 + ", result = " + zumaGame.findMinStep(board8, hand8)); //Expected -1
    }
}

/**
 * Time and Space Complexity Analysis:
 *
 * Time Complexity: O(N * M * 3^N), where N is the length of the board and M is the length of the hand.
 * In the worst-case scenario, we explore all possible insertion positions for each ball in the hand.
 * The reduce() function takes O(N) in the worst case, where N is the length of the board.
 * The number of possible hand states grows exponentially.
 *
 * Space Complexity: O(N + M), where N is the length of the board and M is the length of the hand.
 * O(N) for the recursion stack depth (in the worst case, no reduction is possible)
 * O(M) for the handMap
 * O(N) for the stack used within reduce() method.
 */
