import java.util.Arrays;
import java.util.Stack;

public class NextGreaterElement {

    /**
     * Finds the next greater element to the right for each element in the input array.
     * If no greater element exists to the right, the element is assigned -1.
     *
     * @param arr The input array of integers.
     * @return An array containing the next greater element for each element in the input array.
     *
     * Time Complexity: O(n) - Each element is visited and processed at most twice (once pushed onto the stack and once popped).
     * Space Complexity: O(n) - The stack can potentially hold all elements of the array in the worst-case scenario.
     */
    public static int[] nextGreaterElements(int[] arr) {
        int n = arr.length;
        int[] result = new int[n];
        Arrays.fill(result, -1); // Initialize result array with -1

        Stack<Integer> stack = new Stack<>(); // Stack to store indices of elements

        for (int i = 0; i < n; i++) {
            // While the stack is not empty and the current element is greater than the element at the top of the stack
            while (!stack.isEmpty() && arr[i] > arr[stack.peek()]) {
                int index = stack.pop(); // Pop the index from the stack
                result[index] = arr[i];   // Set the next greater element for that index
            }
            stack.push(i); // Push the current element's index onto the stack
        }

        return result;
    }

    public static void main(String[] args) {
        // Test case 1
