import java.util.*;

public class MaxPathQuality {

    /**
     * Finds the maximum quality path in an undirected graph.  The quality of a
     * path is defined as the minimum weight of any edge on the path.
     *
     * @param graph A map representing the graph where keys are vertices and values
     *              are lists of edges connected to that vertex.  Each edge is
     *              represented as an array of size 2: {neighbor, weight}.
     * @return The maximum quality path in the graph.  Returns 0 if the graph is empty
     *         or contains no edges.
     *
     * Time Complexity: O(E log V) where E is the number of edges and V is the number of vertices.
     *                  This is due to the sorting of edges in descending order by weight and
     *                  the iteration through the edges. The Union-Find operations are nearly constant time.
     * Space Complexity: O(V + E) where V is the number of vertices and E is the number of edges.
     *                   We store the edges in a list, the vertices in a set, and use a Union-Find
     *                   data structure that stores parent and rank information for each vertex.
     */
    public static int maxPathQuality(Map<String, List<int[]>> graph) {
        if (graph == null || graph.isEmpty()) {
            return 0;
        }

        Set<String> vertices = graph.keySet(); // Get all vertices
        List<Edge> edges = new ArrayList<>(); // Store all edges

        // Collect all edges from the graph
        for (String vertex : vertices) {
            List<int[]> neighbors = graph.get(vertex);
            if (neighbors != null) {
                for (int[] neighbor : neighbors) {
                    edges.add(new Edge(vertex, getNeighbor(vertex, graph, neighbor[0]), neighbor[1])); // Create an edge object
                }
            }
        }

        // Sort edges in descending order by weight
        edges.sort(Comparator.comparingInt(Edge -> -Edge.weight));

        UnionFind uf = new UnionFind(vertices); // Initialize Union-Find data structure
        int maxQuality = 0;

        // Iterate through the edges in descending order of weight
        for (Edge edge : edges) {
            // If the two vertices connected by the edge are not already connected, union them
            if (!uf.isConnected(edge.source, edge.destination)) {
                uf.union(edge.source, edge.destination);
                maxQuality = edge.weight; // Update maxQuality to the weight of the current edge
            }
        }

        return maxQuality;
    }

    // Helper method to get the neighboring vertex's String label
    private static String getNeighbor(String source, Map<String, List<int[]>> graph, int neighborIndex) {
        int count = 0;
        for (String vertex : graph.keySet()) {
            if (!vertex.equals(source)) {
                if (count == neighborIndex) {
                    return vertex;
                }
                count++;
            }
        }
        return null;  //Should not happen if the graph is valid. This is defensive.
    }


    // Edge class to represent an edge in the graph
    static class Edge {
        String source;
        String destination;
        int weight;

        public Edge(String source, String destination, int weight) {
            this.source = source;
            this.destination = destination;
            this.weight = weight;
        }
    }

    // Union-Find data structure to detect cycles
    static class UnionFind {
        private Map<String, String> parent;
        private Map<String, Integer> rank;

        public UnionFind(Set<String> vertices) {
            parent = new HashMap<>();
            rank = new HashMap<>();
            for (String vertex : vertices) {
                parent.put(vertex, vertex);  // Initially, each vertex is its own parent
                rank.put(vertex, 0);          // Initially, the rank of each vertex is 0
            }
        }

        // Find the root of the set containing vertex v (path compression)
        public String find(String v) {
            if (!parent.get(v).equals(v)) {
                parent.put(v, find(parent.get(v))); // Path compression
            }
            return parent.get(v);
        }

        // Union two sets containing vertices u and v (union by rank)
        public void union(String u, String v) {
            String rootU = find(u);
            String rootV = find(v);

            if (!rootU.equals(rootV)) {
                if (rank.get(rootU) < rank.get(rootV)) {
                    parent.put(rootU, rootV);
                } else if (rank.get(rootU) > rank.get(rootV)) {
                    parent.put(rootV, rootU);
                } else {
                    parent.put(rootV, rootU);
                    rank.put(rootU, rank.get(rootU) + 1);
                }
            }
        }

        // Check if two vertices are connected (belong to the same set)
        public boolean isConnected(String u, String v) {
            return find(u).equals(find(v));
        }
    }

    public static void main(String[] args) {
        // Example 1
        Map<String, List<int[]>> graph1 = new HashMap<>();
        graph1.put("A", Arrays.asList(new int[]{0, 5}, new int[]{1, 3}));
        graph1.put("B", Arrays.asList(new int[]{0, 5}, new int[]{2, 2}, new int[]{3, 6}, new int[]{4, 7}));
        graph1.put("C", Arrays.asList(new int[]{0, 3}, new int[]{2, 2}, new int[]{5, 4}, new int[]{7, 1}));
        graph1.put("D", Arrays.asList(new int[]{1, 4}, new int[]{7, 4}));
        graph1.put("E", Arrays.asList(new int[]{2, 6}, new int[]{4, 3}));
        graph1.put("F", Arrays.asList(new int[]{3, 7}, new int[]{4, 3}, new int[]{6, 2}));
        graph1.put("G", Arrays.asList(new int[]{5, 2}, new int[]{7, 4}));
        graph1.put("H", Arrays.asList(new int[]{6, 4}, new int[]{1, 1}));

        System.out.println("Maximum quality path (Example 1): " + maxPathQuality(graph1)); // Expected: 5


        // Example 2: Empty graph
        Map<String, List<int[]>> graph2 = new HashMap<>();
        System.out.println("Maximum quality path (Example 2 - Empty graph): " + maxPathQuality(graph2)); // Expected: 0

        // Example 3: Single vertex
        Map<String, List<int[]>> graph3 = new HashMap<>();
        graph3.put("A", new ArrayList<>());
        System.out.println("Maximum quality path (Example 3 - Single vertex): " + maxPathQuality(graph3)); // Expected: 0

        // Example 4: Disconnected graph
        Map<String, List<int[]>> graph4 = new HashMap<>();
        graph4.put("A", Arrays.asList(new int[]{1, 5}));
        graph4.put("B", Arrays.asList(new int[]{0, 5}));
        graph4.put("C", Arrays.asList(new int[]{3, 10}));
        graph4.put("D", Arrays.asList(new int[]{2, 10}));
        System.out.println("Maximum quality path (Example 4 - Disconnected graph): " + maxPathQuality(graph4)); // Expected: 10

        // Example 5: Graph with only one edge
        Map<String, List<int[]>> graph5 = new HashMap<>();
        graph5.put("A", Arrays.asList(new int[]{0, 15}));
        graph5.put("B", Arrays.asList(new int[]{0, 15}));
        System.out.println("Maximum quality path (Example 5 - Single Edge): " + maxPathQuality(graph5)); // Expected: 15

        // Example 6: Graph with a cycle
        Map<String, List<int[]>> graph6 = new HashMap<>();
        graph6.put("A", Arrays.asList(new int[]{1, 8}));
        graph6.put("B", Arrays.asList(new int[]{0, 8}, new int[]{2,5}));
        graph6.put("C", Arrays.asList(new int[]{1, 5}, new int[]{0,3})); // Back edge to A
        System.out.println("Maximum quality path (Example 6 - Graph with cycle): " + maxPathQuality(graph6)); // Expected: 8
    }
}
