import java.util.*;

/**
 * Trie implementation for efficient search queries, autocomplete, and wildcard matching.
 */
public class TrieSearch {

    /**
     * TrieNode represents a node in the Trie data structure.
     */
    static class TrieNode {
        Map<Character, TrieNode> children;
        boolean isWord;

        public TrieNode() {
            children = new HashMap<>();
            isWord = false;
        }
    }

    private TrieNode root;

    /**
     * Constructor to initialize the Trie.
     */
    public TrieSearch() {
        root = new TrieNode();
    }

    /**
     * Inserts a word into the Trie.
     *
     * @param word The word to be inserted.
     *
     * Time Complexity: O(n), where n is the length of the word.
     * Space Complexity: O(n), where n is the length of the word (in the worst case, if the word doesn't share any prefixes with existing words).
     */
    public void insert(String word) {
        TrieNode current = root;
        for (char c : word.toCharArray()) {
            current.children.putIfAbsent(c, new TrieNode());
            current = current.children.get(c);
        }
        current.isWord = true;
    }

    /**
     * Searches for an exact match of a word in the Trie.
     *
     * @param word The word to search for.
     * @return True if the word exists in the Trie, false otherwise.
     *
     * Time Complexity: O(n), where n is the length of the word.
     * Space Complexity: O(1)
     */
    public boolean search(String word) {
        TrieNode current = root;
        for (char c : word.toCharArray()) {
            if (!current.children.containsKey(c)) {
                return false;
            }
            current = current.children.get(c);
        }
        return current.isWord;
    }

    /**
     * Retrieves all words in the Trie that start with the given prefix.
     *
     * @param prefix The prefix to search for.
     * @return A list of words that start with the given prefix, sorted lexicographically.
     *
     * Time Complexity: O(p + k), where p is the length of the prefix and k is the number of nodes in the subtree rooted at the prefix.
     * Space Complexity: O(k), where k is the number of nodes in the subtree rooted at the prefix (for storing the results).
     */
    public List<String> autocomplete(String prefix) {
        TrieNode current = root;
        for (char c : prefix.toCharArray()) {
            if (!current.children.containsKey(c)) {
                return Collections.emptyList(); // No words with this prefix
            }
            current = current.children.get(c);
        }

        List<String> results = new ArrayList<>();
        collectWords(current, prefix, results);
        Collections.sort(results); // Ensure lexicographical order
        return results;
    }

    /**
     * Helper method to recursively collect all words starting from a given node.
     *
     * @param node    The current node in the Trie.
     * @param prefix  The current prefix being built.
     * @param results The list to store the results.
     */
    private void collectWords(TrieNode node, String prefix, List<String> results) {
        if (node.isWord) {
            results.add(prefix);
        }

        for (Map.Entry<Character, TrieNode> entry : node.children.entrySet()) {
            collectWords(entry.getValue(), prefix + entry.getKey(), results);
        }
    }

    /**
     * Searches for words that match the given pattern, where '?' represents a wildcard character.
     *
     * @param pattern The search pattern with wildcards.
     * @return A list of words that match the pattern.
     *
     * Time Complexity: O(N), where N represents the total number of characters in all words stored within the trie. In the worst-case scenario, the algorithm
     *  visits every node in the trie. This time complexity arises from the algorithm's need to exhaustively explore all possible matching words.
     * Space Complexity: O(M), where M represents the number of words that match the search pattern. The space complexity is attributed to storing the list of matching words.
     */
    public List<String> wildcardSearch(String pattern) {
        List<String> results = new ArrayList<>();
        wildcardSearchHelper(root, pattern, 0, new StringBuilder(), results);
        Collections.sort(results); // Ensure lexicographical order
        return results;
    }

    /**
     * Recursive helper method for wildcard search.
     *
     * @param node      The current node in the Trie.
     * @param pattern   The search pattern.
     * @param index     The current index in the pattern.
     * @param currentWord The current word being built.
     * @param results   The list to store the results.
     */
    private void wildcardSearchHelper(TrieNode node, String pattern, int index, StringBuilder currentWord, List<String> results) {
        if (index == pattern.length()) {
            if (node.isWord) {
                results.add(currentWord.toString());
            }
            return;
        }

        char c = pattern.charAt(index);
        if (c == '?') {
            for (Map.Entry<Character, TrieNode> entry : node.children.entrySet()) {
                currentWord.append(entry.getKey());
                wildcardSearchHelper(entry.getValue(), pattern, index + 1, currentWord, results);
                currentWord.deleteCharAt(currentWord.length() - 1); // Backtrack
            }
        } else {
            if (node.children.containsKey(c)) {
                currentWord.append(c);
                wildcardSearchHelper(node.children.get(c), pattern, index + 1, currentWord, results);
                currentWord.deleteCharAt(currentWord.length() - 1); // Backtrack
            }
        }
    }

    /**
     * Main method for testing the Trie implementation.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        TrieSearch trie = new TrieSearch();
        String[] words = {"apple", "application", "apply", "art", "artist", "ball", "bat", "batman"};

        // Insert words into the Trie
        for (String word : words) {
            trie.insert(word);
        }

        // Test exact match search
        System.out.println("Search 'apple': " + trie.search("apple")); // true
        System.out.println("Search 'banana': " + trie.search("banana")); // false

        // Test autocomplete suggestions
        System.out.println("Autocomplete suggestions for 'ap': " + trie.autocomplete("ap")); // [apple, application, apply]
        System.out.println("Autocomplete suggestions for 'ba': " + trie.autocomplete("ba")); // [ball, bat, batman]
        System.out.println("Autocomplete suggestions for 'appli': " + trie.autocomplete("appli")); // [application, apply]
        System.out.println("Autocomplete suggestions for 'xyz': " + trie.autocomplete("xyz")); // []

        // Test wildcard search
        System.out.println("Wildcard search for 'ap?le': " + trie.wildcardSearch("ap?le")); // [apple]
        System.out.println("Wildcard search for 'ba?': " + trie.wildcardSearch("ba?")); // [bat, ball]
        System.out.println("Wildcard search for 'ba??an': " + trie.wildcardSearch("ba??an")); // [batman]
        System.out.println("Wildcard search for 'a??': " + trie.wildcardSearch("a??")); // [art]
        System.out.println("Wildcard search for '??t': " + trie.wildcardSearch("??t")); // [art, bat]

        // Edge case tests
        System.out.println("Autocomplete suggestions for empty prefix: " + trie.autocomplete("")); // All words
        System.out.println("Wildcard search for empty pattern: " + trie.wildcardSearch("")); // []
        System.out.println("Wildcard search with all wildcards '???': " + trie.wildcardSearch("???")); // [art, bat]  (order may differ)
    }
}
