import java.util.Arrays;

public class MaxBitwiseOR {

    /**
     * Calculates the maximum possible bitwise OR value of an array after performing a limited number of doubling operations.
     *
     * @param arr The input array of integers.
     * @param k   The maximum number of doubling operations allowed.
     * @return The maximum possible bitwise OR value of the array.
     *
     * Time Complexity: O(n * k), where n is the length of the array and k is the allowed number of operations.
     * Space Complexity: O(1) - Uses constant extra space.
     */
    public static int maxBitwiseOR(int[] arr, int k) {
        int n = arr.length;
        int maxOR = 0;

        // Iterate through all possible choices of indices to double
        for (int i = 0; i < (1 << n); i++) { // Iterate through all subsets of indices
            if (Integer.bitCount(i) > k) continue; // Skip if the number of set bits (indices) exceeds k

            int currentOR = 0;
            int[] tempArr = Arrays.copyOf(arr, n); // Create a copy of the original array

            int opsUsed = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) { // Check if the j-th bit is set in the subset 'i'
                    //Double element at index j
                    tempArr[j] = tempArr[j] * (1 << Math.min(k, Integer.bitCount(i)));
                    opsUsed = Integer.bitCount(i);
                }
            }
             if(opsUsed <= k){
                  for (int val : tempArr) {
                    currentOR |= val;
                }
                  maxOR = Math.max(maxOR, currentOR);
             }

        }
         return maxOR;
    }

    /**
     * Improved version of maxBitwiseOR. Multiplies the most significant bits.
     *
     * Time Complexity: O(n)
     * Space Complexity: O(1)
     */
    public static int maxBitwiseOROptimized(int[] arr, int k) {
        int n = arr.length;
        int maxVal = 0;
        int maxIndex = 0;

        for (int i = 0; i < n; i++) {
            if (arr[i] > maxVal) {
                maxVal = arr[i];
                maxIndex = i;
            }
        }

        arr[maxIndex] = arr[maxIndex] * (1 << k); // Multiply the largest element by 2^k

        int orVal = 0;
        for (int val : arr) {
            orVal |= val;
        }

        return orVal;
    }


