import java.util.ArrayDeque;
import java.util.Arrays;
import java.util.Deque;

public class SlidingWindowMaximum {

    /**
     * Finds the maximum element in each window of size k as it slides through the array.
     *
     * @param nums The input array of integers.
     * @param k    The window size.
     * @return An array containing the maximum values in each window.  Returns an empty array if nums is null or empty, or if k is invalid.
     * @throws IllegalArgumentException if k is less than or equal to zero, or greater than the array length.
     */
    public int[] maxSlidingWindow(int[] nums, int k) {
        // Edge Cases
        if (nums == null || nums.length == 0) {
            return new int[0];
        }

        if (k <= 0) {
            throw new IllegalArgumentException("Window size k must be greater than 0.");
        }

        if (k > nums.length) {
            throw new IllegalArgumentException("Window size k cannot be greater than the array length.");
        }


        int n = nums.length;
        int[] result = new int[n - k + 1];
        Deque<Integer> deque = new ArrayDeque<>(); // Store indices of elements in the window

        // Iterate through the array
        for (int i = 0; i < n; i++) {
            // Remove elements out of the window
            while (!deque.isEmpty() && deque.peekFirst() <= i - k) {
                deque.pollFirst();
            }

            // Remove smaller elements from the deque
            while (!deque.isEmpty() && nums[deque.peekLast()] <= nums[i]) {
                deque.pollLast();
            }

            // Add current element index to the deque
            deque.offerLast(i);

            // Add maximum element to the result array when the window is full
            if (i >= k - 1) {
                result[i - k + 1] = nums[deque.peekFirst()];
            }
        }

        return result;
    }

    /**
     * Main method for testing the SlidingWindowMaximum class.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        SlidingWindowMaximum solution = new SlidingWindowMaximum();

        // Test Case 1
