        System.out.println("Input: nums = " + Arrays.toString(nums2) + ", k = " + k2);
        System.out.println("Output: " + Arrays.toString(result2)); // Expected: [9, 7, 7, 6, 8]
        System.out.println("--------------------");

        // Test Case 3: Empty array
        int[] nums3 = {};
        int k3 = 1;
