public class DivideIntegers {

    /**
     * Divides two integers without using multiplication, division, or mod operator.
     * Returns the quotient after dividing the dividend by the divisor.
     * If the quotient overflows, return the maximum integer value.
     *
     * Time Complexity: O(log N), where N is the absolute value of the dividend.  The while loop iterates until
     *                  the absolute value of the shifted divisor is greater than the remaining dividend.  The number
     *                  of iterations corresponds to the number of bits in the dividend's binary representation.
     * Space Complexity: O(1). Constant space is used.
     *
     * @param dividend The number to be divided.
     * @param divisor The number to divide by.
     * @return The quotient of the division.
     */
    public int divide(int dividend, int divisor) {
        // Handle edge cases
        if (divisor == 0) {
            return Integer.MAX_VALUE; // Division by zero
        }
        if (dividend == Integer.MIN_VALUE && divisor == -1) {
            return Integer.MAX_VALUE; // Overflow case
        }

        // Determine the sign of the result
        int sign = ((dividend < 0) ^ (divisor < 0)) ? -1 : 1;

        // Convert both dividend and divisor to positive values
        long absDividend = Math.abs((long) dividend);
        long absDivisor = Math.abs((long) divisor);

        // Perform the division using bit manipulation
        long quotient = 0;
        while (absDividend >= absDivisor) {
            long temp = absDivisor;
            long multiple = 1;

            // Find the largest multiple of divisor that is <= dividend
            while (absDividend >= (temp << 1)) {
                if (temp > Integer.MAX_VALUE / 2) break; // Prevent temp from overflowing
                temp <<= 1;
                multiple <<= 1;
            }

            absDividend -= temp;
            quotient += multiple;
        }

        // Apply the sign and return the result
        return (int) (sign * quotient);
    }

    public static void main(String[] args) {
        DivideIntegers divider = new DivideIntegers();

        // Test cases
        System.out.println("10 / 3 = " + divider.divide(10, 3));         // Expected: 3
        System.out.println("7 / -2 = " + divider.divide(7, -2));        // Expected: -3
        System.out.println("-2147483648 / 1 = " + divider.divide(-2147483648, 1));  // Expected: -2147483648
        System.out.println("-2147483648 / -1 = " + divider.divide(-2147483648, -1)); // Expected: 2147483647 (Overflow handling)
        System.out.println("0 / 5 = " + divider.divide(0, 5));          // Expected: 0
        System.out.println("5 / 0 = " + divider.divide(5, 0));          // Expected: 2147483647 (Division by zero)
        System.out.println("1 / 1 = " + divider.divide(1, 1));          // Expected: 1
        System.out.println("-1 / 1 = " + divider.divide(-1, 1));         // Expected: -1
        System.out.println("1 / -1 = " + divider.divide(1, -1));         // Expected: -1
        System.out.println("-1 / -1 = " + divider.divide(-1, -1));        // Expected: 1
        System.out.println("15 / 3 = " + divider.divide(15, 3));        // Expected: 5
        System.out.println("-15 / 3 = " + divider.divide(-15, 3));       // Expected: -5
        System.out.println("15 / -3 = " + divider.divide(15, -3));       // Expected: -5
        System.out.println("-15 / -3 = " + divider.divide(-15, -3));      // Expected: 5
        System.out.println("2147483647 / 1 = " + divider.divide(2147483647, 1)); // Expected 2147483647
        System.out.println("-2147483647 / 1 = " + divider.divide(-2147483647, 1)); // Expected -2147483647
        System.out.println("100 / 10 = " + divider.divide(100, 10));      //Expected: 10
        System.out.println("-100 / 10 = " + divider.divide(-100, 10));      //Expected: -10
        System.out.println("100 / -10 = " + divider.divide(100, -10));      //Expected: -10
        System.out.println("-100 / -10 = " + divider.divide(-100, -10));      //Expected: 10

    }
}
