import java.util.Arrays;

public class MaxGoldPath {

    /**
     * Calculates the maximum amount of gold that can be collected while moving from the top-left cell (1,1)
     * to the bottom-right cell (n,m) of a 2D grid, moving only downwards or rightwards.
     *
     * @param grid A 2D array representing the grid of cells, where each cell contains the amount of gold.
     * @return The maximum amount of gold that can be collected.
     */
    public int maxGold(int[][] grid) {
        // Handle edge cases: null or empty grid.
        if (grid == null || grid.length == 0 || grid[0].length == 0) {
            return 0;
        }

        int n = grid.length;  // Number of rows
        int m = grid[0].length; // Number of columns

        // dp[i][j] stores the maximum gold collected to reach cell (i, j).
        int[][] dp = new int[n][m];

        // Initialize the first cell (0, 0).
        dp[0][0] = grid[0][0];

        // Fill the first row.  We can only move right.
        for (int j = 1; j < m; j++) {
            dp[0][j] = dp[0][j - 1] + grid[0][j];
        }

        // Fill the first column. We can only move down.
        for (int i = 1; i < n; i++) {
            dp[i][0] = dp[i - 1][0] + grid[i][0];
        }

        // Fill the rest of the dp table.  Movements are either from above or from the left.
        for (int i = 1; i < n; i++) {
            for (int j = 1; j < m; j++) {
                dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]) + grid[i][j];
            }
        }

        // The result is stored in the bottom-right cell of the dp table.
        return dp[n - 1][m - 1];
    }


    /**
     * Main method for testing the solution with multiple test cases.
     *
     * @param args Command line arguments (not used).
     */
