public class MaxNonOverlappingPalindromes {

    /**
     * Finds the maximum number of non-overlapping palindromic substrings of length at least k.
     *
     * @param s The input string.
     * @param k The minimum length of a valid palindrome substring.
     * @return The maximum number of non-overlapping palindromic substrings.
     *
     * Time Complexity: O(n^2), where n is the length of the string s.  We iterate to expand palindromes, which takes O(n^2) in the worst case.
     * Space Complexity: O(n), where n is the length of the string s.  This is primarily due to the dp array.
     */
    public int maxNonOverlappingPalindromes(String s, int k) {
        int n = s.length();
        int[] dp = new int[n + 1]; // dp[i] stores the maximum number of palindromes ending at index i-1

        for (int i = 1; i <= n; i++) {
            dp[i] = dp[i - 1]; // Initialize with the value from the previous index (not choosing a new palindrome)

            // Check for odd-length palindromes centered at i-1
            for (int len = 1; len <= i; len++) {
                if (len * 2 - 1 > i) break;
                if (i - len < 0) break;
                int left = i - len;
                int right = i - 1;
                if (isPalindrome(s, left, right) && (right - left + 1) >= k) {
                    dp[i] = Math.max(dp[i], dp[left] + 1);
                }
            }

            // Check for even-length palindromes ending at i-1
            for (int len = 1; len <= i; len++) {
                if (len * 2 > i) break;
                if (i - len < 0) break;
                int left = i - len;
                int right = i - 1;
                if (isPalindrome(s, left, right) && (right - left + 1) >= k) {
                    dp[i] = Math.max(dp[i], dp[left] + 1);
                }
            }


            for (int len = 1; len < i; len++) {
                int left = i - len;
                int right = i - 1;
                if (right - left < 0) continue;

                // Odd length palindrome
                if(i - len - len >= 0) {
                  int left2 = i - len - len;
                  if (isPalindrome(s, left2, right) && (right - left2 + 1) >= k) {
                       dp[i] = Math.max(dp[i], dp[left2] + 1);
                  }
                }


                // Even Length palindrome
                if (i - len - len + 1 >= 0) {
                  int left2 = i - len - len + 1;
                  if (isPalindrome(s, left2, right) && (right - left2 + 1) >= k) {
                     dp[i] = Math.max(dp[i], dp[left2] + 1);
                  }
                }
            }

        }

        return dp[n];
    }

    /**
     * Checks if a substring of s is a palindrome.
     *
     * @param s The input string.
     * @param start The starting index of the substring.
     * @param end The ending index of the substring.
     * @return True if the substring is a palindrome, false otherwise.
     *
     * Time Complexity: O(n), where n is the length of the substring.
     * Space Complexity: O(1).
     */
    private boolean isPalindrome(String s, int start, int end) {
        while (start < end) {
            if (s.charAt(start) != s.charAt(end)) {
                return false;
            }
            start++;
            end--;
        }
        return true;
    }

    public static void main(String[] args) {
        MaxNonOverlappingPalindromes solution = new MaxNonOverlappingPalindromes();

        // Test cases
        String s1 = "abaaba";
        int k1 = 2;
        System.out.println("Test Case 1: s = " + s1 + ", k = " + k1 + ", Result = " + solution.maxNonOverlappingPalindromes(s1, k1)); // Expected: 2

        String s2 = "abcba";
        int k2 = 3;
        System.out.println("Test Case 2: s = " + s2 + ", k = " + k2 + ", Result = " + solution.maxNonOverlappingPalindromes(s2, k2)); // Expected: 1

        String s3 = "madamracecar";
        int k3 = 4;
        System.out.println("Test Case 3: s = " + s3 + ", k = " + k3 + ", Result = " + solution.maxNonOverlappingPalindromes(s3, k3)); // Expected: 2

        String s4 = "aaaaa";
        int k4 = 2;
        System.out.println("Test Case 4: s = " + s4 + ", k = " + k4 + ", Result = " + solution.maxNonOverlappingPalindromes(s4, k4)); // Expected: 2

        String s5 = "a";
        int k5 = 1;
        System.out.println("Test Case 5: s = " + s5 + ", k = " + k5 + ", Result = " + solution.maxNonOverlappingPalindromes(s5, k5)); // Expected: 1

        String s6 = "ab";
        int k6 = 2;
        System.out.println("Test Case 6: s = " + s6 + ", k = " + k6 + ", Result = " + solution.maxNonOverlappingPalindromes(s6, k6)); // Expected: 0

        String s7 = "racecarannaracecar";
        int k7 = 5;
        System.out.println("Test Case 7: s = " + s7 + ", k = " + k7 + ", Result = " + solution.maxNonOverlappingPalindromes(s7, k7)); // Expected: 2

        String s8 = "bananas";
        int k8 = 3;
        System.out.println("Test Case 8: s = " + s8 + ", k = " + k8 + ", Result = " + solution.maxNonOverlappingPalindromes(s8, k8)); // Expected: 1
    }
}
