import java.util.Stack;

public class RemoveAdjacentDuplicates {

    /**
     * Removes adjacent duplicate characters from a string until no more duplicates exist.
     *
     * @param s The input string.
     * @return The string after removing all adjacent duplicates.
     *
     * Time Complexity: O(n), where n is the length of the input string.
     *                  Each character is pushed and popped from the stack at most once.
     * Space Complexity: O(n) in the worst case, when the stack stores almost all characters of the input string.
     */
    public String removeDuplicates(String s) {
        Stack<Character> stack = new Stack<>();

        for (char c : s.toCharArray()) {
            if (!stack.isEmpty() && stack.peek() == c) {
                stack.pop(); // Remove the adjacent duplicate
            } else {
                stack.push(c); // Add the character to the stack
            }
        }

        StringBuilder result = new StringBuilder();
        for (char c : stack) {
            result.append(c);
        }

        return result.toString();
    }

    public static void main(String[] args) {
        RemoveAdjacentDuplicates remover = new RemoveAdjacentDuplicates();

        // Test cases
        String s1 = "abbaca";
        System.out.println("Input: " + s1 + ", Output: " + remover.removeDuplicates(s1)); // Expected: ca

        String s2 = "azxxzy";
        System.out.println("Input: " + s2 + ", Output: " + remover.removeDuplicates(s2)); // Expected: ay

        String s3 = "aaaaaaaa";
        System.out.println("Input: " + s3 + ", Output: " + remover.removeDuplicates(s3)); // Expected: ""

        String s4 = "abcabc";
