import java.util.PriorityQueue;
import java.util.Collections;

public class LastStoneWeight {

    /**
     * Calculates the weight of the last remaining stone after repeatedly smashing the two heaviest stones.
     *
     * @param stones An array of integers representing the weights of the stones.
     * @return The weight of the last remaining stone, or 0 if no stones are left.
     */
    public int lastStoneWeight(int[] stones) {
        if (stones == null || stones.length == 0) {
            return 0;
        }

        // Use a max heap (PriorityQueue) to efficiently retrieve the two heaviest stones.
        PriorityQueue<Integer> maxHeap = new PriorityQueue<>(Collections.reverseOrder());

        // Add all stones to the max heap.
        for (int stone : stones) {
            maxHeap.offer(stone);
        }

        // While there are at least two stones, smash the two heaviest.
        while (maxHeap.size() > 1) {
            int stone1 = maxHeap.poll(); // Heaviest stone
            int stone2 = maxHeap.poll(); // Second heaviest stone

            if (stone1 != stone2) {
                // If they have different weights, the heavier stone survives with reduced weight.
                maxHeap.offer(Math.abs(stone1 - stone2));
            }
            // If they have the same weight, both are destroyed (no need to add anything back to the heap).
        }

        // If there is one stone left, return its weight. Otherwise, return 0.
        return maxHeap.isEmpty() ? 0 : maxHeap.poll();
    }

    public static void main(String[] args) {
        LastStoneWeight stoneWeight = new LastStoneWeight();

        // Test cases
        int[] stones1 = {2, 7, 4, 1, 8, 1};
        System.out.println("Input: [2, 7, 4, 1, 8, 1], Output: " + stoneWeight.lastStoneWeight(stones1)); // Expected: 1

        int[] stones2 = {5, 2, 6, 1};
        System.out.println("Input: [5, 2, 6, 1], Output: " + stoneWeight.lastStoneWeight(stones2)); // Expected: 0

        int[] stones3 = {3, 1, 4, 5, 6, 2};
        System.out.println("Input: [3, 1, 4, 5, 6, 2], Output: " + stoneWeight.lastStoneWeight(stones3)); // Expected: 1

        int[] stones4 = {2};
        System.out.println("Input: [2], Output: " + stoneWeight.lastStoneWeight(stones4)); // Expected: 2

        int[] stones5 = {};
        System.out.println("Input: [], Output: " + stoneWeight.lastStoneWeight(stones5)); // Expected: 0

        int[] stones6 = {1, 1};
        System.out.println("Input: [1, 1], Output: " + stoneWeight.lastStoneWeight(stones6)); // Expected: 0

        int[] stones7 = {7,6,7,6,9};
        System.out.println("Input: [7,6,7,6,9], Output: " + stoneWeight.lastStoneWeight(stones7)); // Expected: 1

        int[] stones8 = {3,7,2};
        System.out.println("Input: [3,7,2], Output: " + stoneWeight.lastStoneWeight(stones8)); // Expected: 2
    }

    /**
     * Time Complexity: O(N log N), where N is the number of stones.
     *                 - Building the max heap takes O(N log N) time.
     *                 - In the worst case, we might need to extract and insert elements into the heap N times,
     *                   each taking O(log N) time.
     *
     * Space Complexity: O(N), where N is the number of stones.
     *                  - The max heap stores all the stones, so the space complexity is proportional to the number of stones.
     */
}
```

