/**
 * A scalable and robust system for website traffic analytics, similar to Google Analytics.
 *
 * This system simulates data collection, processing, and provides real-time insights.
 * It focuses on scalability, high availability, security, and data privacy considerations.
 *
 * Note: This is a simplified, in-memory simulation for demonstration purposes.  A production
 * system would utilize technologies like Kafka, Spark/Flink, Cassandra/Bigtable, and a dedicated
 * dashboarding framework.
 */
public class WebsiteAnalytics {

    // In-memory storage for website data.  In a real system, this would be a distributed database.
    private final Map<String, WebsiteData> websiteDataMap = new ConcurrentHashMap<>();

    /**
     * Represents the data for a single website.
     */
    private static class WebsiteData {
        private final String websiteId;
        private final AtomicLong pageViews = new AtomicLong(0);
        private final Map<String, AtomicLong> pageViewsByPath = new ConcurrentHashMap<>();
        private final Map<String, AtomicLong> eventCounts = new ConcurrentHashMap<>(); // Key: eventName, Value: count
        private final Map<String, AtomicLong> uniqueVisitors = new ConcurrentHashMap<>();//key:user id,value: count
        private final Map<String, Long> lastActiveTime = new ConcurrentHashMap<>(); //key:user id,value: timestamp
        // More metrics can be added here (e.g., clicks, traffic sources, demographics)

        public WebsiteData(String websiteId) {
            this.websiteId = websiteId;
        }

        public long incrementPageViews() {
            return pageViews.incrementAndGet();
        }

        public long incrementPageViews(String pagePath) {
            return pageViewsByPath.computeIfAbsent(pagePath, k -> new AtomicLong(0)).incrementAndGet();
        }
        public long incrementEventCount(String eventName) {
             return eventCounts.computeIfAbsent(eventName, k -> new AtomicLong(0)).incrementAndGet();
        }
        public long addUniqueVisitor(String userId){
            return uniqueVisitors.computeIfAbsent(userId, k -> new AtomicLong(0)).incrementAndGet();
        }
        public void updateLastActive(String userId){
             lastActiveTime.put(userId, System.currentTimeMillis());
        }

        public long getPageViews() {
            return pageViews.get();
        }
        public long getUniqueVisitors(){
            long count=0;
            for(AtomicLong value:uniqueVisitors.values()){
                if(value.get()>0){
                    count++;
                }
            }
            return count;
        }

        public Map<String, Long> getPageViewsByPath() {
            Map<String, Long> result = new HashMap<>();
            pageViewsByPath.forEach((path, count) -> result.put(path, count.get()));
            return result;
        }

        public Map<String, Long> getEventCounts() {
            Map<String, Long> result = new HashMap<>();
            eventCounts.forEach((event, count) -> result.put(event, count.get()));
            return result;
        }

        public Long getLastActive(String userId){
            return lastActiveTime.get(userId);
        }
    }

    /**
     * Collects a page view event.
     *
     * @param websiteId The ID of the website.
     * @param pagePath The path of the page viewed.
     *
     * Time Complexity: O(1) - (average) because of using ConcurrentHashMap
     * Space Complexity: O(1) - adding constant amount of new data to HashMap
     */
    public void collectPageView(String websiteId, String pagePath) {
        WebsiteData websiteData = websiteDataMap.computeIfAbsent(websiteId, WebsiteData::new);
        websiteData.incrementPageViews();
        websiteData.incrementPageViews(pagePath);
    }

    /**
     * Collects a custom event.
     *
     * @param websiteId The ID of the website.
