public class Search2DMatrix {

    /**
     * Searches for a target value in a 2D matrix where each row is sorted in ascending order
     * and the first element of each row is greater than the last element of the previous row.
     *
     * @param matrix The 2D matrix to search in.
     * @param target The target value to search for.
     * @return True if the target is found, false otherwise.
     *
     * Time Complexity: O(log(m*n)), where m is the number of rows and n is the number of columns.
     *                  This is because we perform a binary search on the flattened matrix.
     * Space Complexity: O(1), as we use only constant extra space.
     */
    public boolean searchMatrix(int[][] matrix, int target) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            return false;
        }

        int rows = matrix.length;
        int cols = matrix[0].length;
        int low = 0;
        int high = rows * cols - 1;

        while (low <= high) {
            int mid = low + (high - low) / 2; // Prevent potential overflow
            int row = mid / cols;
            int col = mid % cols;

            if (matrix[row][col] == target) {
                return true;
            } else if (matrix[row][col] < target) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }

        return false;
    }

    public static void main(String[] args) {
        Search2DMatrix searcher = new Search2DMatrix();

        // Test Case 1
        int[][] matrix1 = {
