import java.util.*;

// Class representing a node in the binary tree
class TreeNode {
    int val;
    TreeNode left;
    TreeNode right;

    TreeNode(int val) {
        this.val = val;
    }
}

public class DiagonalTreeTraversal {

    /**
     * Performs a diagonal traversal of the binary tree and returns the list of node values.
     *
     * @param root The root of the binary tree.
     * @return A list of integers representing the diagonal traversal of the tree.
     */
    public List<Integer> diagonalTraversal(TreeNode root) {
        List<Integer> result = new ArrayList<>();
        if (root == null) {
            return result; // Handle the edge case of an empty tree
        }

        Queue<TreeNode> queue = new LinkedList<>();
        queue.offer(root);

        while (!queue.isEmpty()) {
            TreeNode current = queue.poll();

            while (current != null) {
                result.add(current.val); // Add the current node's value to the result list

                // If the current node has a left child, add it to the queue for processing in the next diagonal.
                if (current.left != null) {
                    queue.offer(current.left);
                }

                // Move to the right child to continue traversing the current diagonal.
                current = current.right;
            }
        }

        return result;
    }

    /**
     * Main method for testing the diagonalTraversal method with various test cases.
     *
     * @param args Command line arguments (not used).
     */
    public static void main(String[] args) {
        DiagonalTreeTraversal traversal = new DiagonalTreeTraversal();

        // Test Case 1: Example 1 from the problem description
        TreeNode root1 = new TreeNode(1);
