import java.util.*;

public class NearbyTweets {

    /**
     * Data Model:
     * Tweet: Stores tweet information with geo-location.
     * UserLocation: Stores user's last known location.
     * GeoIndex: An efficient data structure (e.g., GeoHash or QuadTree) to index tweets based on location for fast retrieval.
     */

    // Represents a Tweet
    static class Tweet {
        String tweetId;
        String userId;
        String content;
        double latitude;
        double longitude;
        long timestamp; // Tweet creation timestamp

        public Tweet(String tweetId, String userId, String content, double latitude, double longitude) {
            this.tweetId = tweetId;
            this.userId = userId;
            this.content = content;
            this.latitude = latitude;
            this.longitude = longitude;
            this.timestamp = System.currentTimeMillis(); // Assign current timestamp
        }

        @Override
        public String toString() {
            return "Tweet{" +
                    "tweetId='" + tweetId + '\'' +
                    ", userId='" + userId + '\'' +
                    ", content='" + content + '\'' +
                    ", latitude=" + latitude +
                    ", longitude=" + longitude +
                    '}';
        }
    }

    // Represents User Location
    static class UserLocation {
        String userId;
        double latitude;
        double longitude;
        long timestamp; // Location update timestamp

        public UserLocation(String userId, double latitude, double longitude) {
            this.userId = userId;
            this.latitude = latitude;
            this.longitude = longitude;
            this.timestamp = System.currentTimeMillis();
        }
    }

    // Simplified GeoHash implementation (Can use external libraries like Geohash-java for production)
    static class GeoHash {
        private static final String BASE32 = "0123456789bcdefghjkmnpqrstuvwxyz";

        public static String encode(double latitude, double longitude, int precision) {
            double[] latitudeRange = {-90.0, 90.0};
            double[] longitudeRange = {-180.0, 180.0};
            StringBuilder geohash = new StringBuilder();

            int bit = 0;
            int ch = 0;
            int len = 0;

            while (len < precision) {
                if (bit % 2 == 0) {
                    double mid = (longitudeRange[0] + longitudeRange[1]) / 2;
                    if (longitude >= mid) {
                        ch |= (1 << (4 - (bit % 5)));
                        longitudeRange[0] = mid;
                    } else {
                        longitudeRange[1] = mid;
                    }
                } else {
                    double mid = (latitudeRange[0] + latitudeRange[1]) / 2;
                    if (latitude >= mid) {
                        ch |= (1 << (4 - (bit % 5)));
                        latitudeRange[0] = mid;
                    } else {
                        latitudeRange[1] = mid;
                    }
                }

                bit++;
                if (bit % 5 == 0) {
                    geohash.append(BASE32.charAt(ch));
                    ch = 0;
                    len++;
                }
            }

            return geohash.toString();
        }

        public static double[] decode(String geohash) {
            double[] latitudeRange = {-90.0, 90.0};
            double[] longitudeRange = {-180.0, 180.0};

            boolean even = true;
            for (int i = 0; i < geohash.length(); i++) {
                int charIndex = BASE32.indexOf(geohash.charAt(i));
                for (int j = 4; j >= 0; j--) {
                    int bit = (charIndex >> j) & 1;
                    if (even) {
                        double mid = (longitudeRange[0] + longitudeRange[1]) / 2;
                        if (bit == 1) {
                            longitudeRange[0] = mid;
                        } else {
                            longitudeRange[1] = mid;
                        }
                    } else {
                        double mid = (latitudeRange[0] + latitudeRange[1]) / 2;
                        if (bit == 1) {
                            latitudeRange[0] = mid;
                        } else {
                            latitudeRange[1] = mid;
                        }
                    }
                    even = !even;
                }
            }

            double latitude = (latitudeRange[0] + latitudeRange[1]) / 2;
            double longitude = (longitudeRange[0] + longitudeRange[1]) / 2;
            return new double[]{latitude, longitude};
        }
    }

    // In-memory storage for tweets and user locations (Replace with a database in a real-world scenario)
    private final Map<String, Tweet> tweets = new HashMap<>();
    private final Map<String, UserLocation> userLocations = new HashMap<>();
    private final Map<String, List<Tweet>> geoHashIndex = new HashMap<>(); // Key: GeoHash, Value: List of Tweets

    private static final int GEOHASH_PRECISION = 7; // Adjust for desired granularity

    /**
     * Adds a new tweet to the system and indexes it using GeoHash.
     *
     * @param tweet The tweet to add.
     */
    public void addTweet(Tweet tweet) {
        tweets.put(tweet.tweetId, tweet);
        String geoHash = GeoHash.encode(tweet.latitude, tweet.longitude, GEOHASH_PRECISION);
        geoHashIndex.computeIfAbsent(geoHash, k -> new ArrayList<>()).add(tweet);
    }

    /**
     * Updates the location of a user.
     *
     * @param userLocation The user's new location.
     */
    public void updateUserLocation(UserLocation userLocation) {
        userLocations.put(userLocation.userId, userLocation);
    }

    /**
     * Retrieves nearby tweets for a given user based on their location and a specified radius.
     *
     * @param userId The ID of the user.
     * @param radius The search radius in kilometers.
     * @param maxTweets The maximum number of tweets to return.
     * @return A list of nearby tweets, sorted by recency.
     *
     * Time Complexity:  O(K * log(K) + N), where K is the number of tweets within relevant GeoHashes and N is the
     * number of GeoHashes within the radius. The log(K) factor comes from sorting the tweets by timestamp.
     * In the worst case, N can be large if the radius is very large, but in practice, it's usually bounded.
     * Space Complexity: O(K), where K is the number of tweets within the relevant GeoHashes.  This is mainly due to
     * the space used to store the `nearbyTweets` list and the temporary storage during sorting.
     */
    public List<Tweet> getNearbyTweets(String userId, double radius, int maxTweets) {
        UserLocation userLocation = userLocations.get(userId);
        if (userLocation == null) {
            System.out.println("User location not found for user: " + userId);
            return Collections.emptyList();
        }

        String userGeoHash = GeoHash.encode(userLocation.latitude, userLocation.longitude, GEOHASH_PRECISION);
        List<String> nearbyGeoHashes = getNearbyGeoHashes(userGeoHash);

        List<Tweet> nearbyTweets = new ArrayList<>();
        for (String geoHash : nearbyGeoHashes) {
            List<Tweet> tweetsInGeoHash = geoHashIndex.getOrDefault(geoHash, Collections.emptyList());
            for (Tweet tweet : tweetsInGeoHash) {
                if (isWithinRadius(userLocation.latitude, userLocation.longitude, tweet.latitude, tweet.longitude, radius)) {
                    nearbyTweets.add(tweet);
                }
            }
        }

        // Sort by timestamp (most recent first)
        nearbyTweets.sort(Comparator.comparingLong(tweet -> -tweet.timestamp));

        // Limit the number of tweets returned
        return nearbyTweets.size() > maxTweets ? nearbyTweets.subList(0, maxTweets) : nearbyTweets;
    }

    /**
     * Helper method to get nearby GeoHashes.  For simplicity, this implementation just returns the immediate neighbors.
     * More sophisticated algorithms can be used to find all GeoHashes within a given radius.
     *
     * @param geoHash The GeoHash to find neighbors for.
     * @return A list of nearby GeoHashes.
     *
     * Time Complexity: O(1). The number of neighbors is constant.
     * Space Complexity: O(1). The number of neighbors is fixed.
     */
    private List<String> getNearbyGeoHashes(String geoHash) {
        // In a real-world implementation, you'd use a library or implement a more robust algorithm
        // to find all GeoHashes within the given radius.
        List<String> neighbors = new ArrayList<>();
        neighbors.add(geoHash); // Include the original GeoHash

        // Simplified neighbor calculation (not exhaustive, just for example)
        // In production, you should use a proper GeoHash library.
        double[] decoded = GeoHash.decode(geoHash);
        double lat = decoded[0];
        double lon = decoded[1];

        String north = GeoHash.encode(lat + 0.001, lon, GEOHASH_PRECISION);
        String south = GeoHash.encode(lat - 0.001, lon, GEOHASH_PRECISION);
        String east = GeoHash.encode(lat, lon + 0.001, GEOHASH_PRECISION);
        String west = GeoHash.encode(lat, lon - 0.001, GEOHASH_PRECISION);

        neighbors.add(north);
        neighbors.add(south);
        neighbors.add(east);
        neighbors.add(west);

        return neighbors;
    }

    /**
     * Helper method to check if a tweet is within a given radius of a user. Uses the Haversine formula.
     *
     * @param userLat   The user's latitude.
     * @param userLon   The user's longitude.
     * @param tweetLat  The tweet's latitude.
     * @param tweetLon  The tweet's longitude.
     * @param radius    The radius in kilometers.
     * @return True if the tweet is within the radius, false otherwise.
     *
     * Time Complexity: O(1) - constant time calculation
     * Space Complexity: O(1) - constant space usage
     */
    private boolean isWithinRadius(double userLat, double userLon, double tweetLat, double tweetLon, double radius) {
        final int R = 6371; // Radius of the earth in kilometers

        double latDistance = Math.toRadians(tweetLat - userLat);
        double lonDistance = Math.toRadians(tweetLon - userLon);
        double a = Math.sin(latDistance / 2) * Math.sin(latDistance / 2)
                + Math.cos(Math.toRadians(userLat)) * Math.cos(Math.toRadians(tweetLat))
                * Math.sin(lonDistance / 2) * Math.sin(lonDistance / 2);
        double c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
        double distance = R * c;

        return distance <= radius;
    }

