import java.util.Arrays;

public class ThreeSumClosest {

    /**
     * Finds three integers in the array whose sum is closest to the target.
     *
     * @param arr    The input array of integers.
     * @param target The target sum.
     * @return The sum of the three integers that is closest to the target.
     *
     * Time Complexity: O(n^2), where n is the length of the input array.
     *                   Sorting takes O(n log n), and the two-pointer approach takes O(n^2).
     *                   The two-pointer part dominates.
     * Space Complexity: O(1).  We use constant extra space. If sorting in place isn't allowed then we may need O(n) for sorting depending on the algorithm.
     */
    public int threeSumClosest(int[] arr, int target) {
        if (arr == null || arr.length < 3) {
            throw new IllegalArgumentException("Array must be non-null and have at least 3 elements.");
        }

        Arrays.sort(arr); // Sort the array to use the two-pointer approach.
        int closestSum = arr[0] + arr[1] + arr[2]; // Initialize closestSum with the sum of the first three elements.

        for (int i = 0; i < arr.length - 2; i++) {
            int left = i + 1;
            int right = arr.length - 1;

            while (left < right) {
                int currentSum = arr[i] + arr[left] + arr[right];
                if (currentSum == target) {
                    return target; // Exact match found.
                }

                // Update closestSum if currentSum is closer to the target.
                if (Math.abs(currentSum - target) < Math.abs(closestSum - target)) {
                    closestSum = currentSum;
                }

                if (currentSum < target) {
                    left++; // Increase left to get a larger sum.
                } else {
                    right--; // Decrease right to get a smaller sum.
                }
            }
        }

        return closestSum;
    }

    public static void main(String[] args) {
        ThreeSumClosest solution = new ThreeSumClosest();

        // Test cases
        int[] arr1 = {-1, 2, 1, -4};
        int target1 = 1;
        System.out.println("Input: arr = " + Arrays.toString(arr1) + ", target = " + target1);
        System.out.println("Output: " + solution.threeSumClosest(arr1, target1)); // Expected: 2

        int[] arr2 = {0, 0, 0};
        int target2 = 1;
        System.out.println("Input: arr = " + Arrays.toString(arr2) + ", target = " + target2);
        System.out.println("Output: " + solution.threeSumClosest(arr2, target2)); // Expected: 0

        int[] arr3 = {-1, 2, 1, -6};
        int target3 = 1;
        System.out.println("Input: arr = " + Arrays.toString(arr3) + ", target = " + target3);
        System.out.println("Output: " + solution.threeSumClosest(arr3, target3)); // Expected: 2

        int[] arr4 = {1, 1, -1, -1, 3};
        int target4 = -1;
        System.out.println("Input: arr = " + Arrays.toString(arr4) + ", target = " + target4);
        System.out.println("Output: " + solution.threeSumClosest(arr4, target4)); // Expected: -1

        int[] arr5 = {4, 0, 5, -5, 3, 3, 0, -4, -5};
        int target5 = -2;
        System.out.println("Input: arr = " + Arrays.toString(arr5) + ", target = " + target5);
        System.out.println("Output: " + solution.threeSumClosest(arr5, target5)); // Expected: -2
    }
}
