import java.util.*;
import java.util.stream.Collectors;

public class FrequencySortString {

    /**
     * Sorts a string based on the decreasing frequency of its characters.
     *
     * @param s The input string consisting of lowercase alphabets.
     * @return The sorted string based on character frequency, or an empty string if the input is null or empty.
     *
     * Time Complexity: O(n log k), where n is the length of the string and k is the number of unique characters (at most 26 for lowercase alphabets).
     * Space Complexity: O(k), where k is the number of unique characters. This accounts for the frequency map and the priority queue.
     */
    public String frequencySort(String s) {
        if (s == null || s.isEmpty()) {
            return "";
        }

        // 1. Calculate the frequency of each character.
        Map<Character, Integer> charFrequencyMap = new HashMap<>();
        for (char c : s.toCharArray()) {
            charFrequencyMap.put(c, charFrequencyMap.getOrDefault(c, 0) + 1);
        }

        // 2. Create a priority queue (max heap) to store characters based on their frequency.
        PriorityQueue<Character> maxHeap = new PriorityQueue<>((a, b) -> charFrequencyMap.get(b) - charFrequencyMap.get(a));
        maxHeap.addAll(charFrequencyMap.keySet());

        // 3. Build the sorted string by extracting characters from the priority queue.
        StringBuilder sortedString = new StringBuilder();
        while (!maxHeap.isEmpty()) {
            char c = maxHeap.poll();
            int frequency = charFrequencyMap.get(c);
            for (int i = 0; i < frequency; i++) {
                sortedString.append(c);
            }
        }

        return sortedString.toString();
    }


    public static void main(String[] args) {
        FrequencySortString frequencySortString = new FrequencySortString();

        // Test cases
        String s1 = "cbbcaacbb";
        String result1 = frequencySortString.frequencySort(s1);
        System.out.println("Input: " + s1 + ", Output: " + result1); // Expected output: bbbbcccaa

        String s2 = "jkkkjggg";
        String result2 = frequencySortString.frequencySort(s2);
        System.out.println("Input: " + s2 + ", Output: " + result2); // Expected output: gggkkkjj

        String s3 = "aabbccddeeff";
        String result3 = frequencySortString.frequencySort(s3);
        System.out.println("Input: " + s3 + ", Output: " + result3); // Expected output: aabbccddeeff (order can vary, like bbaa etc.)

        String s4 = "aaaa";
        String result4 = frequencySortString.frequencySort(s4);
        System.out.println("Input: " + s4 + ", Output: " + result4); // Expected output: aaaa

        String s5 = "";
        String result5 = frequencySortString.frequencySort(s5);
        System.out.println("Input: " + s5 + ", Output: " + result5); // Expected output: ""

        String s6 = null;
        String result6 = frequencySortString.frequencySort(s6);
        System.out.println("Input: " + s6 + ", Output: " + result6); // Expected output: ""

        String s7 = "abcde";
        String result7 = frequencySortString.frequencySort(s7);
        System.out.println("Input: " + s7 + ", Output: " + result7); // Expected output: edcba (or any other ordering, but sorted individual frequencies is 1.)
    }
}
