import java.util.*;

public class DetectCycleDirectedGraph {

    /**
     * Detects if a directed graph contains a cycle using Depth-First Search (DFS).
     *
     * @param graph A map representing the directed graph where keys are nodes and values are their adjacent nodes.
     * @return True if the graph contains a cycle, false otherwise.
     */
    public boolean hasCycle(Map<Integer, List<Integer>> graph) {
        if (graph == null || graph.isEmpty()) {
            return false; // Empty graph has no cycles
        }

        int numNodes = graph.size();
        Set<Integer> visited = new HashSet<>(); // Nodes currently in the recursion stack
        Set<Integer> recursionStack = new HashSet<>(); // Nodes visited during the current DFS traversal

        for (Integer node : graph.keySet()) {
            if (detectCycleUtil(graph, node, visited, recursionStack)) {
                return true;
            }
        }

        return false;
    }

    /**
     * Helper function for DFS traversal to detect cycles.
     *
     * @param graph          The graph represented as an adjacency list.
     * @param node           The current node being visited.
     * @param visited        A set to track visited nodes.
     * @param recursionStack A set to track nodes currently in the recursion stack.
     * @return True if a cycle is detected from the current node, false otherwise.
     */
    private boolean detectCycleUtil(Map<Integer, List<Integer>> graph, int node, Set<Integer> visited, Set<Integer> recursionStack) {
        if (recursionStack.contains(node)) {
            return true; // Cycle detected
        }

        if (visited.contains(node)) {
            return false; // Already visited, no cycle from this node
        }

        visited.add(node);
        recursionStack.add(node);

        List<Integer> neighbors = graph.get(node);
        if (neighbors != null) {
            for (Integer neighbor : neighbors) {
                if (detectCycleUtil(graph, neighbor, visited, recursionStack)) {
                    return true;
                }
            }
        }

        recursionStack.remove(node); // Backtrack: remove node from recursion stack
        return false;
    }

    // Main method for testing the solution
    public static void main(String[] args) {
        DetectCycleDirectedGraph cycleDetector = new DetectCycleDirectedGraph();

        // Test Case 1: Graph with a cycle
        Map<Integer, List<Integer>> graph1 = new HashMap<>();
        graph1.put(0, Arrays.asList(1, 2));
        graph1.put(1, Arrays.asList(2));
        graph1.put(2, Arrays.asList(0, 3));
        graph1.put(3, Collections.emptyList());
        System.out.println("Graph 1 has cycle: " + cycleDetector.hasCycle(graph1)); // Expected: true

        // Test Case 2: Graph without a cycle
        Map<Integer, List<Integer>> graph2 = new HashMap<>();
        graph2.put(0, Arrays.asList(1, 2));
        graph2.put(1, Arrays.asList(3));
        graph2.put(2, Arrays.asList(3));
        graph2.put(3, Collections.emptyList());
        System.out.println("Graph 2 has cycle: " + cycleDetector.hasCycle(graph2)); // Expected: false

        // Test Case 3: Single node with a self-loop
        Map<Integer, List<Integer>> graph3 = new HashMap<>();
        graph3.put(0, Arrays.asList(0));
        System.out.println("Graph 3 has cycle: " + cycleDetector.hasCycle(graph3)); // Expected: true

        // Test Case 4: Empty graph
        Map<Integer, List<Integer>> graph4 = new HashMap<>();
        System.out.println("Graph 4 has cycle: " + cycleDetector.hasCycle(graph4)); // Expected: false

        // Test Case 5: Graph with disconnected components and one component with a cycle
        Map<Integer, List<Integer>> graph5 = new HashMap<>();
        graph5.put(0, Arrays.asList(1));
        graph5.put(1, Arrays.asList(2));
        graph5.put(2, Arrays.asList(0));
        graph5.put(3, Collections.emptyList());
        graph5.put(4, Collections.emptyList());
        System.out.println("Graph 5 has cycle: " + cycleDetector.hasCycle(graph5)); // Expected: true

        // Test Case 6: Graph with disconnected components and no cycle
        Map<Integer, List<Integer>> graph6 = new HashMap<>();
        graph6.put(0, Arrays.asList(1));
        graph6.put(1, Arrays.asList(2));
        graph6.put(2, Collections.emptyList());
        graph6.put(3, Arrays.asList(4));
        graph6.put(4, Collections.emptyList());
        System.out.println("Graph 6 has cycle: " + cycleDetector.hasCycle(graph6)); // Expected: false

        // Test Case 7:  Large graph with cycles
        Map<Integer, List<Integer>> graph7 = new HashMap<>();
        for (int i = 0; i < 100; i++) {
            graph7.put(i, new ArrayList<>());
        }
        for(int i = 0; i < 99; i++){
            graph7.get(i).add(i+1);
        }
        graph7.get(99).add(0);
        System.out.println("Graph 7 has cycle: " + cycleDetector.hasCycle(graph7)); // Expected: true
    }


    /**
     * Time and Space Complexity Analysis:
     *
     * - Time Complexity: O(V + E), where V is the number of vertices (nodes) and E is the number of edges in the graph.
     *   This is because the DFS algorithm visits each node and each edge at most once.
     *
     * - Space Complexity: O(V), where V is the number of vertices (nodes).
     *   This is due to the space used by the `visited` and `recursionStack` sets, which can contain at most all the nodes in the graph in the worst-case scenario
     *   (e.g., a deeply nested path or a graph with a large cycle). The recursion depth of the DFS algorithm also contributes to the space complexity.
     *
     */
}
