public class CircularHouseRobber {

    /**
     * Calculates the maximum amount of money the thief can rob from a circular array of houses
     * without alerting the police (robbing adjacent houses).
     *
     * @param nums An array representing the amount of money in each house.
     * @return The maximum amount of money the thief can rob.
     *
     * Time Complexity: O(N) - Two calls to the `rob` helper function, each iterating over the array once.
     * Space Complexity: O(N) - Due to the auxiliary space used by the `Arrays.copyOfRange` in rob function.
     */
    public int robCircular(int[] nums) {
        if (nums == null || nums.length == 0) {
            return 0;
        }

        int n = nums.length;
        if (n == 1) {
            return nums[0];
        }

        // Option 1: Rob the first house, so exclude the last house.
        int case1 = rob(nums, 0, n - 2);

        // Option 2: Don't rob the first house, so include the last house.
        int case2 = rob(nums, 1, n - 1);

        return Math.max(case1, case2);
    }


    /**
     * Helper function to calculate the maximum amount of money that can be robbed from a
     * linear array of houses (standard house robber problem).
     *
     * @param nums  The array representing the amount of money in each house.
     * @param start The starting index of the houses to consider.
     * @param end   The ending index of the houses to consider.
     * @return The maximum amount of money the thief can rob from the specified range of houses.
     *
     * Time Complexity: O(N)
     * Space Complexity: O(N) - due to the use of copyOfRange, can be optimized to O(1).
     */
    private int rob(int[] nums, int start, int end) {
        if (start > end) {
            return 0;
        }

        int n = end - start + 1;
        int[] subArray = new int[n];
        System.arraycopy(nums, start, subArray, 0, n);

        if (n == 0) return 0;
        if (n == 1) return subArray[0];


        int rob1 = subArray[0];
        int rob2 = Math.max(subArray[0], subArray[1]);

        for (int i = 2; i < n; i++) {
            int temp = rob2;
            rob2 = Math.max(rob1 + subArray[i], rob2);
            rob1 = temp;
        }

        return rob2;
    }



    public static void main(String[] args) {
        CircularHouseRobber robber = new CircularHouseRobber();

        // Test cases
        int[] arr1 = {5, 4, 3, 1};
        System.out.println("Input: [5, 4, 3, 1], Output: " + robber.robCircular(arr1)); // Expected: 9 (5 + 4)

        int[] arr2 = {1, 2, 3, 1};
        System.out.println("Input: [1, 2, 3, 1], Output: " + robber.robCircular(arr2)); // Expected: 4

        int[] arr3 = {1, 2, 3};
        System.out.println("Input: [1, 2, 3], Output: " + robber.robCircular(arr3)); // Expected: 3

        int[] arr4 = {1};
        System.out.println("Input: [1], Output: " + robber.robCircular(arr4)); // Expected: 1

        int[] arr5 = {0};
        System.out.println("Input: [0], Output: " + robber.robCircular(arr5)); // Expected: 0

        int[] arr6 = {2, 3, 2};
        System.out.println("Input: [2, 3, 2], Output: " + robber.robCircular(arr6)); // Expected: 3

        int[] arr7 = {4, 1, 2, 7, 5, 3, 1};
        System.out.println("Input: [4, 1, 2, 7, 5, 3, 1], Output: " + robber.robCircular(arr7)); // Expected: 15

        int[] arr8 = {0, 0, 0, 0};
        System.out.println("Input: [0, 0, 0, 0], Output: " + robber.robCircular(arr8)); // Expected: 0

        int[] arr9 = {1, 3, 1, 3, 100};
        System.out.println("Input: [1, 3, 1, 3, 100], Output: " + robber.robCircular(arr9)); // Expected 103

        int[] arr10 = {5, 2, 2, 10};
        System.out.println("Input: [5, 2, 2, 10], Output: " + robber.robCircular(arr10)); // Expected 15
    }
}
