import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Scanner;

public class ExpenseSharingApp {

    // User class to store user details
    static class User {
        String userId;
        String name;
        String email;
        String mobileNumber;

        public User(String userId, String name, String email, String mobileNumber) {
            this.userId = userId;
            this.name = name;
            this.email = email;
            this.mobileNumber = mobileNumber;
        }
    }

    // Enum to represent expense types
    enum ExpenseType {
        EQUAL, EXACT
    }

    // Map to store users
    private static final Map<String, User> users = new HashMap<>();

    // Map to store balances between users.  Key: Payer, Value: Map<Debtor, Amount>
    private static final Map<String, Map<String, Double>> balances = new HashMap<>();

    // Helper function to add a user
    public static void addUser(User user) {
        users.put(user.userId, user);
    }

    // Method to handle expense
    public static void handleExpense(String payerId, double totalAmount, List<String> involvedUsers, ExpenseType expenseType, List<Double> shares) {
        if (!users.containsKey(payerId)) {
            System.out.println("Payer not found: " + payerId);
            return;
        }

        for (String userId : involvedUsers) {
            if (!users.containsKey(userId)) {
                System.out.println("User not found: " + userId);
                return;
            }
        }

        if (expenseType == ExpenseType.EQUAL) {
            double individualShare = Math.round((totalAmount / involvedUsers.size()) * 100.0) / 100.0;  // Round to 2 decimal places

            // Distribute fractional amounts to the first few users to ensure the sum equals the total amount.
            double cumulativeShare = 0.0;
            for (int i = 0; i < involvedUsers.size(); i++) {
                double share = individualShare;
                if (i == 0) {
                    double totalExpectedShare = individualShare * involvedUsers.size();
                    share += (totalAmount - totalExpectedShare);  // Adjust the first user's share
                    share = Math.round(share * 100.0) / 100.0; // Round to 2 places again
                }

                String userId = involvedUsers.get(i);
                cumulativeShare += share;

                if (!payerId.equals(userId)) {  // Don't add balances for the payer paying themselves
                    updateBalance(payerId, userId, share);
                }
            }


        } else if (expenseType == ExpenseType.EXACT) {
            if (shares.size() != involvedUsers.size()) {
                System.out.println("Number of shares does not match the number of users.");
                return;
            }

            double totalShares = 0;
            for (double share : shares) {
                totalShares += share;
            }

            if (Math.abs(totalShares - totalAmount) > 0.01) { // Use a small tolerance to account for potential floating-point errors
                System.out.println("Total shares do not match the total amount.");
                return;
            }

            for (int i = 0; i < involvedUsers.size(); i++) {
                String userId = involvedUsers.get(i);
                if (!payerId.equals(userId)) {
                    updateBalance(payerId, userId, shares.get(i));
                }
            }
        }
    }

    // Helper method to update balance
    private static void updateBalance(String payerId, String debtorId, double amount) {

        balances.computeIfAbsent(payerId, k -> new HashMap<>()).merge(debtorId, amount, Double::sum);
    }

    // Method to show balances for all users
    public static void showBalances() {
        boolean noBalances = true;
        for (String payerId : balances.keySet()) {
            for (String debtorId : balances.get(payerId).keySet()) {
                double amount = balances.get(payerId).get(debtorId);
                if (amount != 0) {
                    noBalances = false;
                    System.out.println(debtorId + " owes " + payerId + ": " + String.format("%.0f", amount));
                }
            }
        }

        if (noBalances) {
            System.out.println("No balances");
        }
    }

    // Method to show balances for a single user
    public static void showBalancesForUser(String userId) {
        boolean noBalances = true;

        // Debts TO the user (user is owed money)
        if (balances.containsKey(userId)) {
            for (Map.Entry<String, Double> entry : balances.get(userId).entrySet()) {
                String debtorId = entry.getKey();
                double amount = entry.getValue();
                if (amount != 0) {
                    noBalances = false;
                    System.out.println(debtorId + " owes " + userId + ": " + String.format("%.0f", amount));
                }
            }
        }

        // Debts OF the user (user owes money)
        for (String payerId : balances.keySet()) {
            if (!payerId.equals(userId) && balances.get(payerId).containsKey(userId)) {
                double amount = balances.get(payerId).get(userId);
                if (amount != 0) {
                    noBalances = false;
                    System.out.println(userId + " owes " + payerId + ": " + String.format("%.0f", amount));
                }
            }
        }

        if (noBalances) {
            System.out.println("No balances");
        }
    }

