import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class PriceDiscount {

    /**
     * Applies a 20% discount to the price found in a string.
     *
     * @param input The input string containing currency and price.
     * @return The modified string with the discounted price, or the original string if no price is found.
     */
    public String applyDiscount(String input) {
        // Regular expression to find a currency symbol followed by a number (price).
        // Matches currency symbols like $, €, £, etc. and handles optional spaces.
        Pattern pattern = Pattern.compile("(\\$|€|£)\\s*(\\d+)");  //Optimized for currency symbols

        Matcher matcher = pattern.matcher(input);

        if (matcher.find()) {
            // Extract the currency and price from the matched groups.
            String currency = matcher.group(1);
            String priceString = matcher.group(2);

            try {
                // Convert the price string to an integer.
                int price = Integer.parseInt(priceString);

                // Calculate the discount (20%).
                double discount = 0.20 * price;

                // Calculate the discounted price.
                int discountedPrice = (int) (price - discount); // Cast to int for consistency with input.

                // Replace the original price with the discounted price in the input string.
                return matcher.replaceFirst(currency + discountedPrice);

            } catch (NumberFormatException e) {
                // Handle the case where the price is not a valid number (e.g., very large number).
                System.err.println("Error parsing price: " + priceString); //Log the error for debugging
                return input; // Return the original string to prevent program crash
            }
        } else {
            // If no price is found, return the original string.
            return input;
        }
    }

