import java.util.Arrays;

public class StringMultiplication {

    /**
     * Multiplies two non-negative integers represented as strings.
     *
     * @param num1 The first number as a string.
     * @param num2 The second number as a string.
     * @return The product of num1 and num2 as a string.
     *
     * Time Complexity: O(m*n), where m and n are the lengths of num1 and num2, respectively.
     * Space Complexity: O(m+n), for storing the intermediate results in the 'product' array.
     */
    public String multiply(String num1, String num2) {
        // Handle edge cases: zero or one of the numbers is zero
        if (num1.equals("0") || num2.equals("0")) {
            return "0";
        }

        int m = num1.length();
        int n = num2.length();
        int[] product = new int[m + n]; // Maximum possible length of product

        // Multiply each digit of num1 with each digit of num2
        for (int i = m - 1; i >= 0; i--) {
            int carry = 0;
            int digit1 = num1.charAt(i) - '0';

            for (int j = n - 1; j >= 0; j--) {
                int digit2 = num2.charAt(j) - '0';
                int currentProduct = digit1 * digit2 + product[i + j + 1] + carry;

                carry = currentProduct / 10;
                product[i + j + 1] = currentProduct % 10;
            }

            product[i] += carry; // Add the remaining carry
        }

        // Build the result string
        StringBuilder result = new StringBuilder();
        for (int digit : product) {
            if (!(result.length() == 0 && digit == 0)) { // Avoid leading zeros
                result.append(digit);
            }
        }

        return result.toString();
    }

    public static void main(String[] args) {
        StringMultiplication multiplier = new StringMultiplication();

        // Test cases
        String num1 = "4154";
        String num2 = "51454";
        System.out.println("Product of " + num1 + " and " + num2 + " is: " + multiplier.multiply(num1, num2)); // Expected: 213739916

