import java.util.*;
import java.util.concurrent.*;

public class StringProcessing {

    /**
     * Processes a list of strings by grouping them based on their starting character
     * and then executing a separate task for each group.
     *
     * @param strings The input list of strings.
     * @throws InterruptedException if any thread is interrupted while waiting.
     */
    public void processStrings(List<String> strings) throws InterruptedException {
        // 1. Group strings by their starting character.  Using a concurrent map to allow for multi-threading without race conditions.
        ConcurrentMap<Character, List<String>> groupedStrings = new ConcurrentHashMap<>();

        for (String str : strings) {
            if (str != null && !str.isEmpty()) { // Handle null or empty strings
                char startChar = Character.toLowerCase(str.charAt(0)); // Use lowercase for case-insensitive grouping

                // Use computeIfAbsent to atomically create the list if it doesn't exist.
                groupedStrings.computeIfAbsent(startChar, k -> new CopyOnWriteArrayList<>()).add(str);
            }
        }

        // 2. Create a thread pool for concurrent processing.
        int numThreads = Math.min(groupedStrings.size(), Runtime.getRuntime().availableProcessors()); // Limit threads to avoid over-subscription.
        ExecutorService executor = Executors.newFixedThreadPool(numThreads);

        // 3. Submit a task to the executor for each group of strings.
        List<Future<?>> futures = new ArrayList<>();
        for (Map.Entry<Character, List<String>> entry : groupedStrings.entrySet()) {
            char startChar = entry.getKey();
            List<String> stringList = entry.getValue();

            // Submit a callable that executes the task (here just printing) and returns void.
            Callable<Void> task = () -> {
                try {
                    // Simulate some actual work being done for each group of strings.
                    // In a real scenario, this would be replaced with a more complex operation.
                    System.out.println("Processing strings starting with '" + startChar + "': " + stringList);
                    Thread.sleep(100); // Simulate work
                    return null; // Return null to fulfill Callable<Void> contract
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt(); // Restore interrupted status
                    throw e;  // Re-throw the exception for handling.
                }
            };

            futures.add(executor.submit(task));
        }

        // 4. Wait for all tasks to complete or handle exceptions.
        for (Future<?> future : futures) {
            try {
                future.get(); // Get the result (null in this case) or throw an exception if the task failed.
            } catch (ExecutionException e) {
                System.err.println("Error during task execution: " + e.getCause());
                // Handle the exception appropriately (e.g., log it, retry, or terminate).
            }
        }


        // 5. Shutdown the executor.
        executor.shutdown();
        executor.awaitTermination(Long.MAX_VALUE, TimeUnit.NANOSECONDS); // Wait until all tasks are finished
    }


    public static void main(String[] args) throws InterruptedException {
        StringProcessing processor = new StringProcessing();

