import java.util.*;
import java.util.stream.Collectors;

public class AutoComplete {

    private List<String> dataSource;  // The list of words to autocomplete from
    private int maxSuggestions = 10;   // Maximum number of suggestions to display
    private static final int DEBOUNCE_DELAY = 300; // Debounce delay in milliseconds

    public AutoComplete(List<String> dataSource) {
        this.dataSource = dataSource;
    }

    public AutoComplete(List<String> dataSource, int maxSuggestions) {
        this.dataSource = dataSource;
        this.maxSuggestions = maxSuggestions;
    }

    /**
     * Retrieves auto-complete suggestions based on the given input prefix.
     *
     * @param prefix The input prefix to search for.
     * @return A list of auto-complete suggestions matching the prefix.
     *
     * Time Complexity: O(n log n) in the worst case (sorting). O(n) in the average case if the data is reasonably well distributed.  Using streams and collections.sort might change the actual timings based on implementation.  Filtering is O(n) in the worst case. The sorting is O(k log k), where k is the number of results, which in our case is bounded by `maxSuggestions` but could approach n in the worst case.
     * Space Complexity: O(k), where k is the number of suggestions returned (at most `maxSuggestions`).
     */
    public List<String> getSuggestions(String prefix) {
        if (prefix == null || prefix.isEmpty()) {
            return Collections.emptyList();
        }

        List<String> suggestions = dataSource.stream()
                .filter(word -> word.toLowerCase().startsWith(prefix.toLowerCase()))
                .limit(maxSuggestions)
                .collect(Collectors.toList());

        // Sort the suggestions alphabetically.  This ensures consistent suggestion order.
        Collections.sort(suggestions);
        return suggestions;
    }



    /**
     * Retrieves auto-complete suggestions using a Trie data structure for improved performance.
     * The TrieNode class is defined as an inner class within the AutoComplete class.
     *
     * @param prefix The input prefix to search for.
     * @return A list of auto-complete suggestions matching the prefix.
     *
     * Time Complexity: O(m + k), where m is the length of the prefix and k is the number of suggestions.
     * Space Complexity: O(k), where k is the number of suggestions returned (at most `maxSuggestions`). O(N) for the Trie itself where N is the total characters in all source words.
     */
    public List<String> getSuggestionsTrie(String prefix) {
        if (prefix == null || prefix.isEmpty()) {
            return Collections.emptyList();
        }

        TrieNode node = searchNode(prefix.toLowerCase());
        List<String> suggestions = new ArrayList<>();

        if (node != null) {
            getWordsFromNode(node, prefix.toLowerCase(), suggestions);
        }

        Collections.sort(suggestions);
        return suggestions.stream().limit(maxSuggestions).collect(Collectors.toList());
    }

    private TrieNode root = new TrieNode();

    /**
     * Builds a Trie data structure from the data source for efficient prefix searching.
     * Time complexity is O(N*M) where N is the number of words and M is the length of each word.
     * Space complexity is O(N*M) because in the worst case, the Trie may contain all the characters of all the words.
     */
    public void buildTrie() {
        for (String word : dataSource) {
            insert(word.toLowerCase());
        }
    }

    /**
     * Inserts a word into the Trie.
     * Time Complexity: O(m), where m is the length of the word.
     * Space Complexity: O(m), in the worst case where a new branch is created for each character of the word.
     */
    private void insert(String word) {
        TrieNode current = root;
        for (int i = 0; i < word.length(); i++) {
            char ch = word.charAt(i);
            TrieNode node = current.children.get(ch);
            if (node == null) {
                node = new TrieNode();
                current.children.put(ch, node);
            }
            current = node;
        }
        current.isWord = true;
    }

    /**
     * Searches for a node in the Trie based on a prefix.
     * Time Complexity: O(m), where m is the length of the prefix.
     * Space Complexity: O(1).
     */
    private TrieNode searchNode(String prefix) {
        TrieNode current = root;
        for (int i = 0; i < prefix.length(); i++) {
            char ch = prefix.charAt(i);
            TrieNode node = current.children.get(ch);
            if (node == null) {
                return null;
            }
            current = node;
        }
        return current;
    }

    /**
     * Helper method to retrieve all words from a given TrieNode.
     * Time Complexity: O(k), where k is the number of words reachable from the node.
     * Space Complexity: O(k), where k is the number of words stored in the suggestions list.
     */
    private void getWordsFromNode(TrieNode node, String prefix, List<String> suggestions) {
        if (node.isWord) {
            suggestions.add(prefix);
        }

        for (char ch : node.children.keySet()) {
            getWordsFromNode(node.children.get(ch), prefix + ch, suggestions);
        }
    }


    // Inner class representing a node in the Trie
    private static class TrieNode {
        Map<Character, TrieNode> children = new HashMap<>();
        boolean isWord = false;
    }

    public static void main(String[] args) throws InterruptedException {
        // Sample data source
        List<String> data = Arrays.asList("apple", "banana", "apricot", "avocado", "pineapple", "orange", "grape", "kiwi", "blackberry", "blueberry");

        // Test Case 1: Basic auto-complete
        AutoComplete autoComplete = new AutoComplete(data, 5);
