import java.util.ArrayList;
import java.util.List;

/**
 * Solution for finding the border nodes of a binary tree in an anti-clockwise fashion.
 * The algorithm involves three steps:
 * 1. Print the root node.
 * 2. Print the left boundary nodes (excluding leaf nodes).
 * 3. Print the leaf nodes (from left to right).
 * 4. Print the right boundary nodes (excluding leaf nodes) in reverse order.
 */
public class BinaryTreeBorder {

    // Represents a node in the binary tree.
    static class Node {
        int data;
        Node left, right;

        Node(int data) {
            this.data = data;
            left = right = null;
        }
    }

    /**
     * Prints the border nodes of the given binary tree in an anti-clockwise manner.
     *
     * @param root The root node of the binary tree.
     * @return A list containing the border nodes in anti-clockwise order.
     */
    public List<Integer> printBorder(Node root) {
        List<Integer> borderNodes = new ArrayList<>();

        if (root == null) {
            return borderNodes; // Handle empty tree
        }

        borderNodes.add(root.data); // Print the root node

        // Print left boundary
        printLeftBoundary(root.left, borderNodes);

        // Print leaf nodes
        printLeaves(root, borderNodes);

        // Print right boundary in reverse order
        List<Integer> rightBoundary = new ArrayList<>();
        printRightBoundary(root.right, rightBoundary);
        for (int i = rightBoundary.size() - 1; i >= 0; i--) {
            borderNodes.add(rightBoundary.get(i));
        }

        return borderNodes;
    }

    /**
     * Prints the left boundary nodes (excluding leaf nodes) in a top-down manner.
     *
     * @param node         The current node being visited.
     * @param borderNodes The list to store the border nodes.
     */
    private void printLeftBoundary(Node node, List<Integer> borderNodes) {
        if (node == null || (node.left == null && node.right == null)) {
            return; // Base case: reached the end or leaf node.
        }

        borderNodes.add(node.data);

        if (node.left != null) {
            printLeftBoundary(node.left, borderNodes); // Traverse left first
        } else {
            printLeftBoundary(node.right, borderNodes); // If no left child, traverse right.
        }
    }

    /**
     * Prints the leaf nodes in a left-to-right manner.
     *
     * @param node         The current node being visited.
     * @param borderNodes The list to store the border nodes.
     */
    private void printLeaves(Node node, List<Integer> borderNodes) {
        if (node == null) {
            return;
        }

        if (node.left == null && node.right == null) {
            borderNodes.add(node.data);
            return;
        }

        printLeaves(node.left, borderNodes);
        printLeaves(node.right, borderNodes);
    }

    /**
     * Prints the right boundary nodes (excluding leaf nodes) in a bottom-up manner (reverse order).
     *
     * @param node          The current node being visited.
     * @param rightBoundary The list to store the right boundary nodes in reverse order.
     */
    private void printRightBoundary(Node node, List<Integer> rightBoundary) {
        if (node == null || (node.left == null && node.right == null)) {
            return; // Base case: reached the end or leaf node.
        }

        if (node.right != null) {
            printRightBoundary(node.right, rightBoundary); // Traverse right first
        } else {
            printRightBoundary(node.left, rightBoundary); // If no right child, traverse left.
        }

        rightBoundary.add(node.data);
    }

    public static void main(String[] args) {
        BinaryTreeBorder borderFinder = new BinaryTreeBorder();

        // Test Case 1: Example Tree
        Node root1 = new Node(100);
        root1.left = new Node(50);
        root1.right = new Node(150);
        root1.left.left = new Node(25);
        root1.left.right = new Node(35);
        root1.right.left = new Node(130);
        root1.right.right = new Node(210);
        root1.left.left.right = new Node(30);
        root1.left.right.left = new Node(70);
        root1.left.right.right = new Node(80);

        System.out.println("Test Case 1: " + borderFinder.printBorder(root1)); // Expected: [100, 50, 25, 30, 70, 80, 35, 130, 210, 150]

        // Test Case 2: Only Root
        Node root2 = new Node(10);
        System.out.println("Test Case 2: " + borderFinder.printBorder(root2)); // Expected: [10]

        // Test Case 3: Left Skewed Tree
        Node root3 = new Node(1);
        root3.left = new Node(2);
        root3.left.left = new Node(3);
        root3.left.left.left = new Node(4);
        System.out.println("Test Case 3: " + borderFinder.printBorder(root3)); // Expected: [1, 2, 3, 4]

        // Test Case 4: Right Skewed Tree
        Node root4 = new Node(1);
        root4.right = new Node(2);
        root4.right.right = new Node(3);
        root4.right.right.right = new Node(4);
        System.out.println("Test Case 4: " + borderFinder.printBorder(root4)); // Expected: [1, 4, 2]

        // Test Case 5: Complete Binary Tree
        Node root5 = new Node(1);
        root5.left = new Node(2);
        root5.right = new Node(3);
        root5.left.left = new Node(4);
        root5.left.right = new Node(5);
        root5.right.left = new Node(6);
        root5.right.right = new Node(7);
        System.out.println("Test Case 5: " + borderFinder.printBorder(root5)); // Expected: [1, 2, 4, 5, 6, 7, 3]

        // Test Case 6: Empty Tree
        Node root6 = null;
        System.out.println("Test Case 6: " + borderFinder.printBorder(root6)); // Expected: []

        // Test Case 7: Tree with only left and right subtrees, no leaves.
        Node root7 = new Node(1);
        root7.left = new Node(2);
        root7.right = new Node(3);
        root7.left.left = new Node(4);
        root7.right.right = new Node(5);

        System.out.println("Test Case 7: " + borderFinder.printBorder(root7));  //Expected [1, 2, 4, 5, 3]
    }
}

/*
 * Time Complexity: O(N), where N is the number of nodes in the tree.
 *   - Each node is visited at most a constant number of times.
 *   - The left boundary, leaf nodes, and right boundary traversals each take O(N) time in the worst case.
 *
 * Space Complexity: O(H), where H is the height of the tree.
 *   - The space is mainly used for the recursion stack during the tree traversals.
 *   - In the worst-case scenario (skewed tree), H can be equal to N, resulting in O(N) space complexity.
 *   - In the best-case scenario (balanced tree), H is log(N), resulting in O(log(N)) space complexity.
 *   - Auxiliary Space: O(N) in worst case for storing the border nodes in an ArrayList.
 */
