import java.util.*;

public class BuyNowPayLater {

    // Constants for credit score thresholds and multiplier
    private static final int GOOD_CREDIT_SCORE = 700;
    private static final int EXCELLENT_CREDIT_SCORE = 750;
    private static final double CREDIT_SCORE_MULTIPLIER = 0.1; //multiplier for spending limit based on credit score

    // Data structures for storing user credit scores and transaction history
    private final Map<String, Integer> userCreditScores = new HashMap<>(); // UserID -> CreditScore
    private final List<Transaction> transactions = new ArrayList<>();
    private final Map<String, Double> userSpendingLimits = new HashMap<>(); // UserID -> Spending Limit

    // User representation
    static class User {
        String userId;
        String name;

        public User(String userId, String name) {
            this.userId = userId;
            this.name = name;
        }
    }

    // Transaction representation
    static class Transaction {
        String transactionId;
        String userId;
        double amount;
        Date transactionDate;
        PaymentSchedule paymentSchedule;


        public Transaction(String transactionId, String userId, double amount, Date transactionDate, PaymentSchedule paymentSchedule) {
            this.transactionId = transactionId;
            this.userId = userId;
            this.amount = amount;
            this.transactionDate = transactionDate;
            this.paymentSchedule = paymentSchedule;
        }

        @Override
        public String toString() {
            return "Transaction{" +
                    "transactionId='" + transactionId + '\'' +
                    ", userId='" + userId + '\'' +
                    ", amount=" + amount +
                    ", transactionDate=" + transactionDate +
                    ", paymentSchedule=" + paymentSchedule +
                    '}';
        }
    }

    // Payment Schedule Representation
    static class PaymentSchedule {
        List<PaymentInstallment> installments;

        public PaymentSchedule(List<PaymentInstallment> installments) {
            this.installments = installments;
        }

        @Override
        public String toString() {
            return "PaymentSchedule{" +
                    "installments=" + installments +
                    '}';
        }
    }

    static class PaymentInstallment {
        Date dueDate;
        double amountDue;

        public PaymentInstallment(Date dueDate, double amountDue) {
            this.dueDate = dueDate;
            this.amountDue = amountDue;
        }

        @Override
        public String toString() {
            return "PaymentInstallment{" +
                    "dueDate=" + dueDate +
                    ", amountDue=" + amountDue +
                    '}';
        }
    }


    /**
     * Updates the credit score for a given user.
     *
     * @param userId      The ID of the user.
     * @param creditScore The new credit score for the user.
     *                    Time Complexity: O(1)
     *                    Space Complexity: O(1)
     */
    public void updateCreditScore(String userId, int creditScore) {
        userCreditScores.put(userId, creditScore);
    }

    /**
     * Retrieves the credit score for a given user.
     *
     * @param userId The ID of the user.
     * @return The credit score of the user, or -1 if the user is not found.
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public int getCreditScore(String userId) {
        return userCreditScores.getOrDefault(userId, -1);
    }

    /**
     * Checks if a user is eligible for Buy Now Pay Later based on their credit score.
     * The user needs to have at least GOOD_CREDIT_SCORE to be eligible
     *
     * @param userId The ID of the user.
     * @return True if the user is eligible, false otherwise.
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public boolean isEligible(String userId) {
        int creditScore = getCreditScore(userId);
        return creditScore >= GOOD_CREDIT_SCORE;
    }

    /**
     * Calculates the spending limit for a user based on their credit score.
     *
     * The base spending limit is credit score * CREDIT_SCORE_MULTIPLIER, but it could be extended with other factors.
     * @param userId The ID of the user.
     * @return The spending limit for the user.
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     */
    public double calculateSpendingLimit(String userId) {
        int creditScore = getCreditScore(userId);
        if (creditScore == -1) {
            return 0.0; // User not found, return 0 limit.
        }
        double spendingLimit = creditScore * CREDIT_SCORE_MULTIPLIER;
        userSpendingLimits.put(userId, spendingLimit);  // Store limit for the user
        return spendingLimit;
    }

    /**
     * Creates a payment schedule for a given purchase, with equal installments monthly.
     *
     * @param amount       The total amount of the purchase.
     * @param numInstallments The number of installments for the payment schedule.
     * @return The payment schedule for the purchase.
     * Time Complexity: O(numInstallments)
     * Space Complexity: O(numInstallments)
     */
    public PaymentSchedule generatePaymentSchedule(double amount, int numInstallments) {
        List<PaymentInstallment> installments = new ArrayList<>();
        double installmentAmount = amount / numInstallments;
        Calendar calendar = Calendar.getInstance(); // Start date is today

        for (int i = 0; i < numInstallments; i++) {
            calendar.add(Calendar.MONTH, 1); // Add one month for each installment
            Date dueDate = calendar.getTime();
            installments.add(new PaymentInstallment(dueDate, installmentAmount));
        }

        return new PaymentSchedule(installments);
    }

    /**
     * Processes a purchase for a user using Buy Now Pay Later.
     *
     * @param userId      The ID of the user making the purchase.
     * @param amount      The amount of the purchase.
     * @param numInstallments the number of installments for paying back the amount
     * @return True if the purchase was successful, false otherwise (e.g., insufficient spending limit, user not eligible).
     * Time Complexity: O(numInstallments) - due to payment schedule generation
     * Space Complexity: O(numInstallments) - due to payment schedule storage.
     */
    public boolean processPurchase(String userId, double amount, int numInstallments) {
        if (!isEligible(userId)) {
            System.out.println("User " + userId + " is not eligible for Buy Now Pay Later.");
            return false;
        }

        double spendingLimit = userSpendingLimits.getOrDefault(userId, calculateSpendingLimit(userId)); // retrieve or calculate the limit

        // Calculate outstanding balance
        double outstandingBalance = 0;
        for(Transaction transaction : transactions){
            if(transaction.userId.equals(userId)){
                outstandingBalance += transaction.amount;
            }
        }

        double availableLimit = spendingLimit - outstandingBalance;

        if (amount > availableLimit) {
            System.out.println("Insufficient spending limit for user " + userId + ". Available limit: " + availableLimit);
            return false;
        }

        PaymentSchedule paymentSchedule = generatePaymentSchedule(amount, numInstallments);
        String transactionId = UUID.randomUUID().toString();  // unique ID for transaction
        Transaction transaction = new Transaction(transactionId, userId, amount, new Date(), paymentSchedule);
        transactions.add(transaction);

        System.out.println("Purchase successful for user " + userId + ". Transaction ID: " + transactionId);
        return true;
    }

    /**
     * Gets all the transactions for the given user
     * @param userId the Id of the User
     * @return List of transactions of the User
     *
     * Time Complexity : O(N) - N is the number of total transactions
     * Space Complexity : O(M) - M is number of transactions for that user
     */
    public List<Transaction> getUserTransactions(String userId){
        List<Transaction> userTransactions = new ArrayList<>();
        for(Transaction transaction : transactions){
            if(transaction.userId.equals(userId)){
                userTransactions.add(transaction);
            }
        }
        return userTransactions;
    }

    /**
