import java.util.List;
import java.util.ArrayList;

public class RotatedSortedArraySearch {

    /**
     * Searches for a target value in a rotated sorted array.
     *
     * @param nums   The rotated sorted array of distinct integers.
     * @param target The target value to search for.
     * @return The index of the target value if it exists in the array; otherwise, -1.
     *
     * Time Complexity: O(log n) - Binary search is used.
     * Space Complexity: O(1) - Constant extra space is used.
     */
    public int searchInRotatedArray(List<Integer> nums, int target) {
        int left = 0;
        int right = nums.size() - 1;

        while (left <= right) {
            int mid = left + (right - left) / 2; // Prevent potential integer overflow

            if (nums.get(mid) == target) {
                return mid;
            }

            // Determine if the left half is sorted
            if (nums.get(left) <= nums.get(mid)) {
                // If the left half is sorted, check if the target is within the left half
                if (target >= nums.get(left) && target < nums.get(mid)) {
                    right = mid - 1; // Target is in the left half
                } else {
                    left = mid + 1;  // Target is in the right half
                }
            } else {
                // If the left half is not sorted, the right half must be sorted
                // Check if the target is within the right half
                if (target > nums.get(mid) && target <= nums.get(right)) {
                    left = mid + 1;  // Target is in the right half
                } else {
                    right = mid - 1; // Target is in the left half
                }
            }
        }

        return -1; // Target not found
    }

    public static void main(String[] args) {
        RotatedSortedArraySearch searcher = new RotatedSortedArraySearch();

