import java.util.Random;

class LargestBSTInBinaryTree {

    static class Node {
        int data;
        Node left, right;

        Node(int data) {
            this.data = data;
            left = right = null;
        }
    }

    static class BSTInfo {
        int size;
        int min;
        int max;
        boolean isBST;

        BSTInfo(int size, int min, int max, boolean isBST) {
            this.size = size;
            this.min = min;
            this.max = max;
            this.isBST = isBST;
        }
    }

    static class LargestBSTResult {
        Node largestBSTNode;
        int largestBSTSize;

        LargestBSTResult(Node largestBSTNode, int largestBSTSize) {
            this.largestBSTNode = largestBSTNode;
            this.largestBSTSize = largestBSTSize;
        }
    }

    /**
     * Finds the largest BST within a given binary tree.
     *
     * @param root The root of the binary tree.
     * @return The root of the largest BST found in the tree.  Returns null if the tree is empty.
     */
    public Node largestBST(Node root) {
        LargestBSTResult result = largestBSTHelper(root);
        return result.largestBSTNode;
    }

    /**
     * Recursive helper function to find the largest BST.
     *
     * @param node The current node being visited.
     * @return A LargestBSTResult object containing the root of the largest BST found
     * in the subtree rooted at the current node, and the size of that BST.
     *
     * Time Complexity: O(N), where N is the number of nodes in the tree.
     *                  Each node is visited once.
     * Space Complexity: O(H), where H is the height of the tree, due to recursion stack.
     *                   In the worst case (skewed tree), H = N, resulting in O(N) space.
     */
    private LargestBSTResult largestBSTHelper(Node node) {
        if (node == null) {
            return new LargestBSTResult(null, 0);
        }

        // Post-order traversal
        LargestBSTResult leftResult = largestBSTHelper(node.left);
        LargestBSTResult rightResult = largestBSTHelper(node.right);

        // Check if the current node can form a BST
        if (leftResult.largestBSTNode == (node.left) && rightResult.largestBSTNode == (node.right) &&
                (node.left == null || node.data > findMax(node.left)) &&
                (node.right == null || node.data < findMin(node.right))) {

            int currentSize = leftResult.largestBSTSize + rightResult.largestBSTSize + 1;
            return new LargestBSTResult(node, currentSize);
        } else {
            // If the current node cannot form a BST, return the larger BST from the subtrees
            if (leftResult.largestBSTSize >= rightResult.largestBSTSize) {
                return leftResult;
            } else {
                return rightResult;
            }
        }
    }

    // Helper function to find the maximum value in a BST
    private int findMax(Node node) {
        while (node.right != null) {
            node = node.right;
        }
        return node.data;
    }

    // Helper function to find the minimum value in a BST
    private int findMin(Node node) {
        while (node.left != null) {
            node = node.left;
        }
        return node.data;
    }

    /**
     * Prints the inorder traversal of a binary tree. Used for testing purposes.
     *
     * @param root The root of the binary tree.
     */
    public void inorderTraversal(Node root) {
        if (root != null) {
            inorderTraversal(root.left);
            System.out.print(root.data + " ");
            inorderTraversal(root.right);
        }
    }

