import java.util.Arrays;
import java.util.List;
import java.util.stream.Collectors;

public class NextPermutation {

    /**
     * Finds the next lexicographically greater permutation of a list of integers.
     * If no such permutation exists, it rearranges the list to the smallest permutation (ascending order).
     *
     * @param nums The list of integers to be rearranged.  This list will be modified in-place.
     * @return The modified list representing the next permutation.
     *
     * Time Complexity: O(n), where n is the length of the input list.
     * Space Complexity: O(1), as the algorithm operates in-place.
     */
    public List<Integer> nextPermutation(List<Integer> nums) {
        int n = nums.size();

        // 1. Find the first decreasing element from the right
        int i = n - 2;
        while (i >= 0 && nums.get(i) >= nums.get(i + 1)) {
            i--;
        }

        // 2. If no decreasing element found, reverse the entire list (smallest permutation)
        if (i < 0) {
            reverse(nums, 0, n - 1);
            return nums;
        }

        // 3. Find the smallest element to the right of i that is greater than nums[i]
        int j = n - 1;
        while (nums.get(j) <= nums.get(i)) {
            j--;
        }

        // 4. Swap nums[i] and nums[j]
        swap(nums, i, j);

        // 5. Reverse the portion of the list to the right of i
        reverse(nums, i + 1, n - 1);

        return nums;
    }


    /**
     * Reverses a portion of the list in-place.
     *
     * @param nums  The list to be modified.
     * @param start The starting index (inclusive) of the portion to be reversed.
     * @param end   The ending index (inclusive) of the portion to be reversed.
     */
    private void reverse(List<Integer> nums, int start, int end) {
        while (start < end) {
            swap(nums, start, end);
            start++;
            end--;
        }
    }

    /**
     * Swaps two elements in the list in-place.
     *
     * @param nums The list to be modified.
     * @param i    The index of the first element to be swapped.
     * @param j    The index of the second element to be swapped.
     */
    private void swap(List<Integer> nums, int i, int j) {
        int temp = nums.get(i);
        nums.set(i, nums.get(j));
        nums.set(j, temp);
    }


    public static void main(String[] args) {
        NextPermutation nextPermutation = new NextPermutation();

        // Test cases
        List<Integer> nums1 = Arrays.asList(1, 2, 3);
        System.out.println("Input: " + nums1);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums1));  // Expected: [1, 3, 2]
        System.out.println();

        List<Integer> nums2 = Arrays.asList(3, 2, 1);
        System.out.println("Input: " + nums2);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums2));  // Expected: [1, 2, 3]
        System.out.println();

        List<Integer> nums3 = Arrays.asList(1, 1, 5); //Example given in the problem - testing duplicate elements
        System.out.println("Input: " + nums3);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums3));  // Expected: [1, 5, 1]
        System.out.println();

        List<Integer> nums4 = Arrays.asList(1, 5, 8, 4, 7, 6, 5, 3, 1);
        System.out.println("Input: " + nums4);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums4));  // Expected: [1, 5, 8, 4, 7, 6, 6, 1, 3]
        System.out.println();

        List<Integer> nums5 = Arrays.asList(5, 4, 3, 2, 1); // Edge case: largest possible permutation
        System.out.println("Input: " + nums5);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums5));  // Expected: [1, 2, 3, 4, 5]
        System.out.println();

        List<Integer> nums6 = Arrays.asList(1); // Edge case: single element
        System.out.println("Input: " + nums6);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums6));  // Expected: [1]
        System.out.println();

        List<Integer> nums7 = Arrays.asList(1, 3, 2);
        System.out.println("Input: " + nums7);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums7));  // Expected: [2, 1, 3]
        System.out.println();

        List<Integer> nums8 = Arrays.asList(2,3,1);
        System.out.println("Input: " + nums8);
        System.out.println("Next Permutation: " + nextPermutation.nextPermutation(nums8));  //Expected: [3, 1, 2]
