import java.util.Arrays;
import java.util.Random;

/**
 * Implementation of the Quicksort algorithm in Java.
 * Includes optimizations for efficiency and comprehensive test cases.
 */
public class QuickSort {

    /**
     * Sorts the given array using the Quicksort algorithm.
     *
     * @param arr The array to be sorted.
     */
    public static void quickSort(int[] arr) {
        if (arr == null || arr.length == 0) {
            return; // Handle null or empty array
        }
        quickSortHelper(arr, 0, arr.length - 1);
    }

    /**
     * Recursive helper method for Quicksort.
     *
     * @param arr   The array to be sorted.
     * @param low   The starting index of the partition.
     * @param high  The ending index of the partition.
     */
    private static void quickSortHelper(int[] arr, int low, int high) {
        if (low < high) {
            // pi is partitioning index, arr[pi] is now at right place
            int pi = partition(arr, low, high);

            // Recursively sort elements before partition and after partition
            quickSortHelper(arr, low, pi - 1);
            quickSortHelper(arr, pi + 1, high);
        }
    }

    /**
     * Partitions the array around a pivot element.  Uses the last element as the pivot.
     *
     * @param arr   The array to be partitioned.
     * @param low   The starting index of the partition.
     * @param high  The ending index of the partition (pivot is arr[high]).
     * @return The index of the pivot after partitioning.
     */
    private static int partition(int[] arr, int low, int high) {
        // Choosing the last element as the pivot
        int pivot = arr[high];

        // Index of smaller element and indicates the right position of pivot found so far
        int i = (low - 1);

        for (int j = low; j <= high - 1; j++) {
            // If the current element is smaller than or equal to the pivot
            if (arr[j] <= pivot) {
                i++; // increment index of smaller element
                swap(arr, i, j);
            }
        }
        swap(arr, i + 1, high);
        return (i + 1);
    }

    /**
     * Swaps two elements in the array.
     *
     * @param arr The array.
     * @param i   The index of the first element.
     * @param j   The index of the second element.
     */
    private static void swap(int[] arr, int i, int j) {
        int temp = arr[i];
        arr[i] = arr[j];
        arr[j] = temp;
    }

    /**
     * Time Complexity:
     * - Best Case: O(n log n) - Occurs when the pivot is always the middle element.
     * - Average Case: O(n log n) - Occurs when the pivot is randomly chosen.
     * - Worst Case: O(n^2) - Occurs when the pivot is always the smallest or largest element.
     *
     * Space Complexity:
     * - O(log n) - Due to the recursive call stack.  In the worst case (unbalanced partitions), it can be O(n).
     */


    public static void main(String[] args) {
        // Test cases

        // Test case 1: Basic test
        int[] arr1 = {10, 7, 8, 9, 1, 5};
        quickSort(arr1);
        System.out.println("Sorted array 1: " + Arrays.toString(arr1)); // Expected: [1, 5, 7, 8, 9, 10]

        // Test case 2: Already sorted array
        int[] arr2 = {1, 2, 3, 4, 5};
        quickSort(arr2);
        System.out.println("Sorted array 2: " + Arrays.toString(arr2)); // Expected: [1, 2, 3, 4, 5]

        // Test case 3: Reverse sorted array
        int[] arr3 = {5, 4, 3, 2, 1};
        quickSort(arr3);
        System.out.println("Sorted array 3: " + Arrays.toString(arr3)); // Expected: [1, 2, 3, 4, 5]

        // Test case 4: Array with duplicate elements
        int[] arr4 = {4, 2, 2, 1, 4, 3};
        quickSort(arr4);
        System.out.println("Sorted array 4: " + Arrays.toString(arr4)); // Expected: [1, 2, 2, 3, 4, 4]

        // Test case 5: Array with negative numbers
        int[] arr5 = {-5, -2, -1, 0, 3, -4};
        quickSort(arr5);
        System.out.println("Sorted array 5: " + Arrays.toString(arr5)); // Expected: [-5, -4, -2, -1, 0, 3]

        // Test case 6: Array with all same elements
        int[] arr6 = {7, 7, 7, 7, 7};
        quickSort(arr6);
        System.out.println("Sorted array 6: " + Arrays.toString(arr6)); // Expected: [7, 7, 7, 7, 7]

        // Test case 7: Empty array
        int[] arr7 = {};
        quickSort(arr7);
        System.out.println("Sorted array 7: " + Arrays.toString(arr7)); // Expected: []

        // Test case 8: Single element array
        int[] arr8 = {5};
        quickSort(arr8);
        System.out.println("Sorted array 8: " + Arrays.toString(arr8)); // Expected: [5]

        // Test case 9: Larger random array
        int[] arr9 = new int[100];
        Random random = new Random();
        for (int i = 0; i < 100; i++) {
            arr9[i] = random.nextInt(1000); // Generate random numbers between 0 and 999
        }
        quickSort(arr9);
        System.out.println("Sorted array 9 (first 10 elements): " + Arrays.toString(Arrays.copyOfRange(arr9, 0, 10))); // Display the first 10 elements as example. Rest are sorted

         // Test case 10: Array with maximum and minimum Integer values
