import java.util.*;
import java.util.concurrent.atomic.AtomicInteger;

public class LoadBalancer {

    private final List<String> backendServers; // List of backend server URLs
    private final Map<String, Boolean> serverHealth; // Tracks server health (URL -> boolean)
    private final AtomicInteger currentServerIndex; // Tracks the next server to use for Round Robin
    private final HealthCheck healthCheck; // Health check mechanism

    private static final int HEALTH_CHECK_INTERVAL = 5000; // Health check interval in milliseconds
    private final boolean sessionPersistence; // Flag for session persistence

    private final Map<String, String> sessionMap; // Map to store session affinity (sessionId -> serverUrl)

    public LoadBalancer(List<String> backendServers, boolean sessionPersistence) {
        if (backendServers == null || backendServers.isEmpty()) {
            throw new IllegalArgumentException("Backend servers cannot be null or empty.");
        }
        this.backendServers = new ArrayList<>(backendServers); // Create a copy to prevent external modification
        this.serverHealth = new HashMap<>();
        this.currentServerIndex = new AtomicInteger(0);
        this.sessionPersistence = sessionPersistence;
        this.sessionMap = new HashMap<>();

        // Initialize server health to true for all servers
        for (String server : backendServers) {
            serverHealth.put(server, true);
        }

        this.healthCheck = new HealthCheck(this);
        healthCheck.startHealthChecks();  // Start health checks in background thread
    }


    /**
     * Load balancing algorithm: Round Robin (with health checks)
     *
     * Time Complexity: O(N) in worst case where N is the number of backend servers.  This is because we might have to iterate through all servers if some are unhealthy.
     *                  However, in the average case when most servers are healthy, it will be closer to O(1).
     * Space Complexity: O(1)
     *
     * @param sessionId (optional) Session ID for session persistence
     * @return URL of the selected backend server, or null if no healthy servers are available
     */
    public String getServer(String sessionId) {
        if (sessionPersistence && sessionId != null && sessionMap.containsKey(sessionId)) {
            String serverUrl = sessionMap.get(sessionId);
            if (serverHealth.getOrDefault(serverUrl, false)) {  // Check if server is still healthy
                return serverUrl;
            } else {
                sessionMap.remove(sessionId);  // Remove stale session entry
            }
        }

        List<String> healthyServers = new ArrayList<>();
        for (String server : backendServers) {
            if (serverHealth.getOrDefault(server, false)) {
                healthyServers.add(server);
            }
        }

        if (healthyServers.isEmpty()) {
            System.err.println("No healthy servers available.");
            return null; // No healthy servers
        }

        // Round Robin selection amongst healthy servers
        int index = currentServerIndex.getAndIncrement() % healthyServers.size();
        String selectedServer = healthyServers.get(index);

        if (sessionPersistence && sessionId != null) {
            sessionMap.put(sessionId, selectedServer);
        }

        return selectedServer;
    }


    /**
     * Updates the health status of a backend server.  This method is called by the HealthCheck thread.
     *
     * Time Complexity: O(1)
     * Space Complexity: O(1)
     *
     * @param serverUrl URL of the server
     * @param isHealthy True if the server is healthy, false otherwise
     */
    public void updateServerHealth(String serverUrl, boolean isHealthy) {
        serverHealth.put(serverUrl, isHealthy);
        System.out.println("Server " + serverUrl + " health status: " + isHealthy);
    }


    /**
     *  HealthCheck class to monitor the health of backend servers.  Runs in a separate thread.
     */
    private static class HealthCheck {
        private final LoadBalancer loadBalancer;

        public HealthCheck(LoadBalancer loadBalancer) {
            this.loadBalancer = loadBalancer;
        }

        public void startHealthChecks() {
            // Use a daemon thread so the application can exit normally
            Thread healthCheckThread = new Thread(() -> {
                while (true) {
                    for (String server : loadBalancer.backendServers) {
                        boolean isHealthy = performHealthCheck(server); // Implement the actual health check logic here
                        loadBalancer.updateServerHealth(server, isHealthy);
                    }
                    try {
                        Thread.sleep(HEALTH_CHECK_INTERVAL);
                    } catch (InterruptedException e) {
                        Thread.currentThread().interrupt();
                        return; // Exit thread if interrupted
                    }
                }
            });
            healthCheckThread.setDaemon(true);
            healthCheckThread.start();
        }

