        // Test Case 1: Basic Load Balancing with no Session Persistence
        System.out.println("Test Case 1: Basic Load Balancing with no Session Persistence");
        List<String> servers1 = Arrays.asList("http://server1.com", "http://server2.com", "http://server3.com");
