import java.util.HashMap;
import java.util.Map;

public class LongestSubstringWithKDistinct {

    /**
     * Finds the length of the longest substring of a given string that contains at most k distinct characters.
     *
     * @param s The input string.
     * @param k The maximum number of distinct characters allowed in the substring.
     * @return The length of the longest substring containing at most k distinct characters.
     *
     * Time Complexity: O(n), where n is the length of the string s.  The while loop within the for loop doesn't iterate
     *                     more than n times in total because the `left` pointer only moves forward.
     * Space Complexity: O(k), where k is the maximum number of distinct characters allowed.  The `charFrequencyMap`
     *                      stores at most k + 1 distinct characters at any given time.
     */
    public int longestSubstringWithKDistinctCharacters(String s, int k) {
        if (s == null || s.isEmpty() || k <= 0) {
            return 0;
        }

        int left = 0;  // Left pointer of the sliding window
        int right = 0; // Right pointer of the sliding window
        int maxLength = 0;  // Length of the longest substring found so far

        // Map to store the frequency of each character in the current window
        Map<Character, Integer> charFrequencyMap = new HashMap<>();

        while (right < s.length()) {
            char rightChar = s.charAt(right);
            charFrequencyMap.put(rightChar, charFrequencyMap.getOrDefault(rightChar, 0) + 1);

            // If the number of distinct characters in the window exceeds k, shrink the window
            while (charFrequencyMap.size() > k) {
                char leftChar = s.charAt(left);
                charFrequencyMap.put(leftChar, charFrequencyMap.get(leftChar) - 1);

                // If the frequency of the left character becomes 0, remove it from the map
                if (charFrequencyMap.get(leftChar) == 0) {
                    charFrequencyMap.remove(leftChar);
                }

                left++; // Move the left pointer to the right
            }

            // Update the maximum length
            maxLength = Math.max(maxLength, right - left + 1);
            right++; // Move the right pointer to the right
        }

        return maxLength;
    }

    public static void main(String[] args) {
