import java.util.HashMap;
import java.util.Map;

public class NumberToWords {

    private static final Map<Integer, String> lessThan20 = new HashMap<>();
    private static final Map<Integer, String> tens = new HashMap<>();
    private static final Map<Integer, String> thousands = new HashMap<>();

    static {
        lessThan20.put(0, "");
        lessThan20.put(1, "One");
        lessThan20.put(2, "Two");
        lessThan20.put(3, "Three");
        lessThan20.put(4, "Four");
