        lessThan20.put(10, "Ten");
        lessThan20.put(11, "Eleven");
        lessThan20.put(12, "Twelve");
        lessThan20.put(13, "Thirteen");
        lessThan20.put(14, "Fourteen");
        lessThan20.put(15, "Fifteen");
        lessThan20.put(16, "Sixteen");
        lessThan20.put(17, "Seventeen");
        lessThan20.put(18, "Eighteen");
        lessThan20.put(19, "Nineteen");

        tens.put(2, "Twenty");
        tens.put(3, "Thirty");
        tens.put(4, "Forty");
        tens.put(5, "Fifty");
        tens.put(6, "Sixty");
        tens.put(7, "Seventy");
        tens.put(8, "Eighty");
        tens.put(9, "Ninety");

        thousands.put(1, "Thousand");
        thousands.put(2, "Million");
        thousands.put(3, "Billion");
    }

    /**
     * Converts a non-negative integer to its English word representation.
     *
     * @param num The integer to convert.
     * @return The English word representation of the integer.
     *
     * Time Complexity: O(1) -  The number of operations is limited by the number of digits in an integer, which is constant.
     * Space Complexity: O(1) - The space used by the maps and string builder is constant.
     */
    public String numberToWords(int num) {
        if (num == 0) {
            return "Zero";
        }

        StringBuilder result = new StringBuilder();
        int i = 1;
        while (num > 0) {
            if (num % 1000 != 0) {
                result.insert(0, helper(num % 1000) + thousands.getOrDefault(i, "") + " ");
            }
            num /= 1000;
            i++;
        }

        return result.toString().trim();
    }

    private String helper(int num) {
        if (num == 0) {
            return "";
        } else if (num < 20) {
            return lessThan20.get(num) + " ";
        } else if (num < 100) {
            return tens.get(num / 10) + " " + helper(num % 10);
        } else {
            return lessThan20.get(num / 100) + " Hundred " + helper(num % 100);
        }
    }


    public static void main(String[] args) {
        NumberToWords converter = new NumberToWords();

        // Test cases
        System.out.println("0: " + converter.numberToWords(0));
        System.out.println("1: " + converter.numberToWords(1));
        System.out.println("10: " + converter.numberToWords(10));
        System.out.println("12: " + converter.numberToWords(12));
        System.out.println("20: " + converter.numberToWords(20));
        System.out.println("23: " + converter.numberToWords(23));
        System.out.println("100: " + converter.numberToWords(100));
        System.out.println("123: " + converter.numberToWords(123));
        System.out.println("1000: " + converter.numberToWords(1000));
        System.out.println("1001: " + converter.numberToWords(1001));
        System.out.println("12345: " + converter.numberToWords(12345));
        System.out.println("1234567: " + converter.numberToWords(1234567));
        System.out.println("11023579: " + converter.numberToWords(11023579));
        System.out.println("50868: " + converter.numberToWords(50868));
        System.out.println("1000000: " + converter.numberToWords(1000000));
        System.out.println("1000000000: " + converter.numberToWords(1000000000));
        System.out.println("Integer.MAX_VALUE: " + converter.numberToWords(Integer.MAX_VALUE));
        System.out.println("100001000: " + converter.numberToWords(100001000)); //testing 100 million and 1000
    }
}
