import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class DistinctElementsInSubarray {

    /**
     * Calculates the count of distinct elements in every subarray of size k.
     *
     * @param arr The input array of integers.
     * @param k   The size of the subarray.
     * @return A list containing the count of distinct elements for each subarray of size k.
     * Returns an empty list if the input array is null or empty or k is invalid.
     *
     * Time Complexity: O(n), where n is the length of the array.  We iterate through the array once.
     * Space Complexity: O(k), in the worst case, all elements in the window are distinct, so the HashMap stores k elements.
     */
    public List<Integer> countDistinctElementsInSubarrays(int[] arr, int k) {
        List<Integer> result = new ArrayList<>();

        // Handle edge cases: null or empty array, invalid k.
        if (arr == null || arr.length == 0 || k <= 0 || k > arr.length) {
            return result;
        }

        // Use a HashMap to store the frequency of each element in the current window.
        Map<Integer, Integer> elementFrequencyMap = new HashMap<>();

        // Process the first window of size k.
        for (int i = 0; i < k; i++) {
            elementFrequencyMap.put(arr[i], elementFrequencyMap.getOrDefault(arr[i], 0) + 1);
        }
        result.add(elementFrequencyMap.size());

        // Slide the window and update the distinct count.
        for (int i = k; i < arr.length; i++) {
            // Remove the leftmost element from the previous window.
            int leftMostElement = arr[i - k];
            elementFrequencyMap.put(leftMostElement, elementFrequencyMap.get(leftMostElement) - 1);
            if (elementFrequencyMap.get(leftMostElement) == 0) {
                elementFrequencyMap.remove(leftMostElement);
            }

            // Add the new rightmost element to the current window.
            int rightMostElement = arr[i];
            elementFrequencyMap.put(rightMostElement, elementFrequencyMap.getOrDefault(rightMostElement, 0) + 1);

            // Add the distinct count for the current window to the result.
            result.add(elementFrequencyMap.size());
        }

        return result;
    }

    public static void main(String[] args) {
