import java.util.LinkedList;
import java.util.Queue;

public class SnakeLadder {

    /**
     * Represents a node in the breadth-first search.  Stores the cell number and the number of dice throws to reach it.
     */
    static class QueueEntry {
        int cellNumber;
        int distance;

        public QueueEntry(int cellNumber, int distance) {
            this.cellNumber = cellNumber;
            this.distance = distance;
        }
    }


    /**
     * Finds the minimum number of dice throws required to reach the destination on a snake and ladder board.
     *
     * @param A       The number of rows on the board.
     * @param B       The number of columns on the board.
     * @param snakes  A 2D array representing snakes, where each row is {head, tail}.
     * @param ladders A 2D array representing ladders, where each row is {bottom, top}.
     * @return The minimum number of dice throws, or -1 if the destination is unreachable.
     */
    public int minDiceThrows(int A, int B, int[][] snakes, int[][] ladders) {
        int boardSize = A * B;

        // Create arrays to store snake and ladder positions
        int[] board = new int[boardSize + 1]; // 1-based indexing
        for (int i = 0; i <= boardSize; i++) {
            board[i] = i; //initialize
        }


        // Populate snakes
        if (snakes != null) {
            for (int[] snake : snakes) {
                board[snake[0]] = snake[1];
            }
        }

        // Populate ladders
        if (ladders != null) {
            for (int[] ladder : ladders) {
                board[ladder[0]] = ladder[1];
            }
        }


        // Breadth-First Search to find the shortest path
        boolean[] visited = new boolean[boardSize + 1];
        Queue<QueueEntry> queue = new LinkedList<>();

        QueueEntry start = new QueueEntry(1, 0);  // Start from cell 1 with 0 throws
        visited[1] = true;
        queue.offer(start);

        while (!queue.isEmpty()) {
            QueueEntry current = queue.poll();
            int cellNumber = current.cellNumber;

            // If we reach the destination, return the distance
            if (cellNumber == boardSize) {
                return current.distance;
            }

            // Try all possible dice throws (1 to 6)
            for (int diceThrow = 1; diceThrow <= 6; diceThrow++) {
                int nextCell = cellNumber + diceThrow;

                if (nextCell <= boardSize) {

                    // Check for snake or ladder
                    nextCell = board[nextCell];

                    //If not visited add in the queue
                    if (!visited[nextCell]) {
                        visited[nextCell] = true;
                        QueueEntry nextEntry = new QueueEntry(nextCell, current.distance + 1);
                        queue.offer(nextEntry);
                    }
                }
            }
        }

        // Destination is unreachable
        return -1;
    }

    public static void main(String[] args) {
        SnakeLadder snakeLadder = new SnakeLadder();

        // Test case 1
        int A1 = 5, B1 = 6;
        int[][] snakes1 = {{7, 4}, {16, 2}, {29, 11}};
        int[][] ladders1 = {{3, 19}, {22, 28}};
        System.out.println("Test Case 1: " + snakeLadder.minDiceThrows(A1, B1, snakes1, ladders1)); // Expected: 3

        // Test case 2: No snakes or ladders
        int A2 = 5, B2 = 6;
        int[][] snakes2 = null;
        int[][] ladders2 = null;
        System.out.println("Test Case 2: " + snakeLadder.minDiceThrows(A2, B2, snakes2, ladders2)); // Expected: 5

        // Test case 3: Simple board, one snake and one ladder
        int A3 = 2, B3 = 2;
        int[][] snakes3 = {{4, 1}};
        int[][] ladders3 = {{2, 3}};
        System.out.println("Test Case 3: " + snakeLadder.minDiceThrows(A3, B3, snakes3, ladders3)); // Expected: 1

        // Test case 4: Unreachable destination due to snakes
        int A4 = 3, B4 = 3;
        int[][] snakes4 = {{2, 1}, {3, 1}, {4, 1}, {5, 1}, {6, 1}, {7, 1}, {8, 1}};
        int[][] ladders4 = null;
        System.out.println("Test Case 4: " + snakeLadder.minDiceThrows(A4, B4, snakes4, ladders4)); // Expected: -1

        // Test case 5: Large board
        int A5 = 10, B5 = 10;
        int[][] snakes5 = {{99, 1}};
        int[][] ladders5 = {{2, 98}};
        System.out.println("Test Case 5: " + snakeLadder.minDiceThrows(A5, B5, snakes5, ladders5)); // Expected: 1

        // Test case 6: Empty snakes and ladders
        int A6 = 3, B6 = 3;
        int[][] snakes6 = {};
        int[][] ladders6 = {};
        System.out.println("Test Case 6: " + snakeLadder.minDiceThrows(A6, B6, snakes6, ladders6)); // Expected: 2

        // Test case 7: All snakes
        int A7 = 3, B7 = 3;
        int[][] snakes7 = {{2, 1}, {3, 1}, {4, 1}, {5, 1}, {6, 1}, {7, 1}, {8, 1}, {9, 1}};
        int[][] ladders7 = {};
        System.out.println("Test Case 7: " + snakeLadder.minDiceThrows(A7, B7, snakes7, ladders7)); // Expected: -1
    }


    /**
     *  Time Complexity Analysis:
     *  - O(A*B) in the worst case, where A and B are the dimensions of the board. This is because the breadth-first search
     *    explores each cell on the board at most once. The inner loop iterates a maximum of 6 times (dice throws).  The
     *    initial snake/ladder population takes O(S + L) time, where S is the number of snakes and L is the number of ladders,
