import java.io.BufferedWriter;
import java.io.FileWriter;
import java.io.IOException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.EnumMap;
import java.util.HashMap;
import java.util.Map;

public class Logger {

    // Enum to represent message levels with priority order
    public enum Level {
        FATAL, ERROR, WARN, INFO, DEBUG
    }

    // Interface for different types of sinks
    public interface Sink {
        void log(String message);
    }

    // Concrete implementation of Sink for logging to a file
    public static class FileSink implements Sink {
        private final String filePath;
        private BufferedWriter writer;

        public FileSink(String filePath) {
            this.filePath = filePath;
            try {
                this.writer = new BufferedWriter(new FileWriter(filePath, true)); // Append mode
            } catch (IOException e) {
                System.err.println("Error creating file writer: " + e.getMessage());
                this.writer = null; // Handle error gracefully
            }
        }

        @Override
        public void log(String message) {
            if (writer != null) {
                try {
                    writer.write(message);
                    writer.newLine();
                    writer.flush(); // Flush to ensure immediate writing
                } catch (IOException e) {
                    System.err.println("Error writing to file: " + e.getMessage());
                }
            } else {
                System.err.println("File sink is not initialized properly.");
            }
        }

        //Optional cleanup method
        public void close() {
            if (writer != null) {
                try {
                    writer.close();
                } catch (IOException e) {
                    System.err.println("Error closing file writer: " + e.getMessage());
                }
            }
        }
    }

    // Concrete implementation of Sink for logging to the console
    public static class ConsoleSink implements Sink {
        @Override
        public void log(String message) {
            System.out.println(message);
        }
    }

    // Logger configuration class
    public static class LoggerConfig {
        private String timeFormat = "yyyy-MM-dd HH:mm:ss";
        private Level loggingLevel = Level.INFO;
        private final Map<Level, Sink> sinks = new EnumMap<>(Level.class); // Use EnumMap for efficiency

        public LoggerConfig() {}

        public String getTimeFormat() {
            return timeFormat;
        }

        public void setTimeFormat(String timeFormat) {
            this.timeFormat = timeFormat;
        }

        public Level getLoggingLevel() {
            return loggingLevel;
        }

        public void setLoggingLevel(Level loggingLevel) {
            this.loggingLevel = loggingLevel;
        }

        public void addSink(Level level, Sink sink) {
            sinks.put(level, sink);
        }

        public Map<Level, Sink> getSinks() {
             return this.sinks;
        }
    }



    private final LoggerConfig config;
    private final SimpleDateFormat dateFormat;

    public Logger(LoggerConfig config) {
        this.config = config;
        this.dateFormat = new SimpleDateFormat(config.getTimeFormat());
    }

    // Main logging method
    public void log(Level level, String namespace, String messageContent) {
        // Check if the message level is above the configured logging level
        if (level.ordinal() <= config.getLoggingLevel().ordinal()) {
            // Get the sink for the given level
            Sink sink = config.getSinks().get(level);

            //If no sink is found, do nothing
            if(sink == null) {
              return;
            }

            // Enrich the message with timestamp and namespace
            String timestamp = dateFormat.format(new Date());
            String enrichedMessage = String.format("%s [%s] [%s] %s", timestamp, level, namespace, messageContent);

            // Log the message to the sink
            sink.log(enrichedMessage);
        }
    }

    public static void main(String[] args) {
        // Example usage
        LoggerConfig config = new LoggerConfig();
        config.setTimeFormat("yyyy-MM-dd HH:mm:ss.SSS"); // More precise timestamp

        // Set logging level
        config.setLoggingLevel(Level.DEBUG);  // Log all levels

        // Create file sink and console sink
        FileSink fileSink = new FileSink("application.log");
        ConsoleSink consoleSink = new ConsoleSink();

        // Associate sinks with levels
        config.addSink(Level.ERROR, fileSink);
        config.addSink(Level.WARN, fileSink);
        config.addSink(Level.INFO, consoleSink);
        config.addSink(Level.DEBUG, consoleSink);
        config.addSink(Level.FATAL, fileSink);


        // Create Logger instance
        Logger logger = new Logger(config);

        // Log messages
        logger.log(Level.INFO, "MainApp", "Application started");
        logger.log(Level.DEBUG, "Database", "Connected to database");
        logger.log(Level.WARN, "Security", "Possible XSS attack detected");
        logger.log(Level.ERROR, "Network", "Connection timeout");
        logger.log(Level.FATAL, "System", "Critical system error");

        // Test case 1: Logging level filtering
        System.out.println("\nTest Case 1: Logging level filtering (WARN)");
        config.setLoggingLevel(Level.WARN);
        logger.log(Level.INFO, "Test", "This should not be logged");
        logger.log(Level.WARN, "Test", "This should be logged");

        // Test case 2: Different sinks for different levels
        System.out.println("\nTest Case 2: Different sinks");
        logger.log(Level.ERROR, "Test", "This should go to file");
        logger.log(Level.INFO, "Test", "This should go to console");

        // Test case 3: Empty message
        System.out.println("\nTest Case 3: Empty message");
        logger.log(Level.DEBUG, "Test", "");

        // Test case 4: Long message
        System.out.println("\nTest Case 4: Long message");
        StringBuilder longMessage = new StringBuilder();
        for (int i = 0; i < 1000; i++) {
            longMessage.append("A");
        }
        logger.log(Level.INFO, "Test", longMessage.toString());

        // Test case 5: Null namespace
        System.out.println("\nTest Case 5: Null namespace");
        logger.log(Level.DEBUG, null, "Message with null namespace");

        //Test case 6: No sink for Level
        System.out.println("\nTest Case 6: No sink associated for level");
        config.getSinks().remove(Level.DEBUG);
        logger.log(Level.DEBUG, "Test", "This should not log anywhere");

        //Cleanup the FileSink
        fileSink.close();
    }
}

/*
Time Complexity Analysis:

- Logger.log(): O(1) -  The complexity is constant because the operations performed (level check, message formatting, and sink logging) take constant time regardless of the input size.  The EnumMap access is also O(1).

Space Complexity Analysis:

- LoggerConfig: O(N) -  The space complexity depends on the number of levels (N) for which sinks are configured.  The `sinks` map in LoggerConfig stores the sinks associated with each level.  However, since the number of levels is fixed (FATAL, ERROR, WARN, INFO, DEBUG),  we can consider N to be constant in practice, leading to O(1) space complexity.
- Logger.log(): O(M) - Where M is the length of the message content.  Message formatting involves creating new strings, so the space required depends on the length of the input message content.  The timestamp and namespace also contribute to the message length but their length is bounded.
*/
```

