import java.util.Random;
import java.util.Scanner;

public class CricketGame {

    private static final int MAX_WICKETS = 10;
    private static final String[] SHOT_OPTIONS = {"defend", "straight drive", "cover drive"};
    private static final Random random = new Random();
    private static final Scanner scanner = new Scanner(System.in);

    private String player1Name;
    private String player2Name;
    private int player1Score;
    private int player2Score;
    private int player1Wickets;
    private int player2Wickets;
    private boolean player1Batting;
    private int targetScore;
    private int currentInning;

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    public CricketGame() {
        player1Score = 0;
        player2Score = 0;
        player1Wickets = 0;
        player2Wickets = 0;
        currentInning = 1;
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    public void startGame() {
        System.out.println("Welcome to the Text-Based Cricket Game!");
        getPlayersNames();
        chooseWhoBatsFirst();
        playGame();
        askToPlayAgain();
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void getPlayersNames() {
        System.out.print("Enter Player 1 name: ");
        player1Name = scanner.nextLine();
        System.out.print("Enter Player 2 name: ");
        player2Name = scanner.nextLine();
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void chooseWhoBatsFirst() {
        player1Batting = random.nextBoolean();
        System.out.println((player1Batting ? player1Name : player2Name) + " will bat first.");
    }

    // Time Complexity: O(n), where n is the number of balls bowled in two innings.
    // Space Complexity: O(1)
    private void playGame() {
        playInning(1);
        swapRoles();
        playInning(2);
        determineWinner();
    }

    // Time Complexity: O(n), where n is the number of balls bowled in the inning.
    // Space Complexity: O(1)
    private void playInning(int inningNumber) {
        currentInning = inningNumber;
        System.out.println("\nInning " + inningNumber + " begins!");
        int wickets = (inningNumber == 1) ? player1Wickets : player2Wickets;

        while (wickets < MAX_WICKETS) {
            displayCurrentStatus();
            String shot = getPlayerShot();
            int runs = bowlAndGetRuns(shot);

            if (runs == -1) {
                System.out.println("OUT!");
                wickets++;
                if(inningNumber == 1) {
                    player1Wickets = wickets;
                }
                else{
                    player2Wickets = wickets;
                }

                if (inningNumber == 2 && isChasingSuccessful()) {
                    break;
                }

            } else {
                System.out.println("Runs scored: " + runs);
                if (player1Batting) {
                    player1Score += runs;
                } else {
                    player2Score += runs;
                }

                if (inningNumber == 2 && isChasingSuccessful()) {
                    break;
                }
            }

            if (wickets == MAX_WICKETS) {
                 System.out.println("All out!");
                break;
            }
        }

        if (inningNumber == 1) {
            targetScore = player1Score + 1;
            System.out.println("\nInning 1 over. Target for " + (player1Batting ? player2Name : player1Name) + " is " + targetScore);

        }
        else{
             System.out.println("\nInning 2 over.");
        }
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void swapRoles() {
        player1Batting = !player1Batting;
        System.out.println("\nSwapping roles!");
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void determineWinner() {
        System.out.println("\nGame Over!");
        System.out.println(player1Name + " score: " + player1Score + " runs, " + player1Wickets + " wickets.");
        System.out.println(player2Name + " score: " + player2Score + " runs, " + player2Wickets + " wickets.");

        if (player1Score > player2Score) {
            System.out.println(player1Name + " wins!");
        } else if (player2Score > player1Score) {
            System.out.println(player2Name + " wins!");
        } else {
            System.out.println("It's a tie!");
        }
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void displayCurrentStatus() {
        String batsmanName = player1Batting ? player1Name : player2Name;
        String bowlerName = player1Batting ? player2Name : player1Name;

        System.out.println("\nCurrent Batsman: " + batsmanName);
        System.out.println("Current Bowler: " + bowlerName);

        if (currentInning == 2) {
            System.out.println("Target to chase: " + targetScore);
        }

        if (player1Batting) {
            System.out.println(player1Name + " score: " + player1Score + " runs, " + player1Wickets + " wickets.");
        } else {
            System.out.println(player2Name + " score: " + player2Score + " runs, " + player2Wickets + " wickets.");
        }
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private String getPlayerShot() {
        String shot;
        while (true) {
            System.out.print("Enter your shot (defend, straight drive, cover drive): ");
            shot = scanner.nextLine().toLowerCase();
            boolean isValid = false;
            for (String option : SHOT_OPTIONS) {
                if (shot.equals(option)) {
                    isValid = true;
                    break;
                }
            }
            if (isValid) {
                break;
            } else {
                System.out.println("Invalid shot! Please choose from: defend, straight drive, cover drive.");
            }
        }
        return shot;
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private int bowlAndGetRuns(String shot) {
        int randomOutcome = random.nextInt(4); // 0, 1, 2, 3
        if ((shot.equals("defend") && randomOutcome == 0) ||
            (shot.equals("straight drive") && randomOutcome == 1) ||
            (shot.equals("cover drive") && randomOutcome == 2)) {
            return -1; // Wicket
        } else {
            return random.nextInt(7); // 0 to 6 runs
        }
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private boolean isChasingSuccessful() {
        if (currentInning == 2) {
            if (player1Batting) {
                return player2Score >= targetScore;
            } else {
                return player1Score >= targetScore;
            }
        }
        return false;
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void askToPlayAgain() {
        System.out.print("Do you want to play again? (yes/no): ");
        String answer = scanner.nextLine().toLowerCase();
        if (answer.equals("yes")) {
            resetGame();
            startGame();
        } else {
            System.out.println("Thanks for playing!");
        }
    }

    // Time Complexity: O(1)
    // Space Complexity: O(1)
    private void resetGame() {
        player1Score = 0;
        player2Score = 0;
        player1Wickets = 0;
        player2Wickets = 0;
        currentInning = 1;
    }

    public static void main(String[] args) {
        CricketGame game = new CricketGame();
        game.startGame();

        // Test Cases (Illustrative - can be expanded)

        // Test Case 1: Normal game flow
        // Players enter names, a player bats first, the game proceeds through two innings,
        // and a winner is declared.

        // Test Case 2: Tie game
        // Modify the runs to result in a tie.

        // Test Case 3: Player gets all out very quickly
        // Simulate a scenario where a player loses all wickets very quickly.
        // This can be achieved by modifying the `bowlAndGetRuns` method to have a higher
