import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class AnagramSearch {

    /**
     * Finds all occurrences of anagrams of string 's' in string 't'.
     *
     * @param t The text string to search within.
     * @param s The string whose anagrams are to be searched for.
     * @return A list of indices where anagrams of 's' are found in 't'.
     *         Returns an empty list if no anagrams are found or if input is invalid.
     */
    public List<Integer> findAnagrams(String t, String s) {
        List<Integer> result = new ArrayList<>();

        // Edge cases: null or empty strings, or s longer than t.
        if (t == null || s == null || t.isEmpty() || s.isEmpty() || s.length() > t.length()) {
            return result;
        }

        int n = t.length();
        int m = s.length();

        // Frequency array for string 's'.  Crucial for optimization.
        int[] sFreq = new int[26]; // Assuming only lowercase English letters

        // Frequency array for the current window in 't'.
        int[] tFreq = new int[26]; // Assuming only lowercase English letters

        // Calculate frequency of characters in string 's'.
        for (int i = 0; i < m; i++) {
            sFreq[s.charAt(i) - 'A']++; // Modified to handle uppercase letters, adapt according to the expected input.
        }

        // Slide a window of size 'm' through 't'.
        for (int i = 0; i < n; i++) {
            // Add the current character from 't' to the window's frequency array.
            tFreq[t.charAt(i) - 'A']++;

            // If the window size exceeds 'm', remove the leftmost character's frequency.
            if (i >= m) {
                tFreq[t.charAt(i - m) - 'A']--;
            }

            // Compare the frequency arrays. If they are equal, it's an anagram.
            if (i >= m - 1) { // Ensure window is of size 'm'
                if (Arrays.equals(sFreq, tFreq)) {
                    result.add(i - m + 1); // Add the starting index of the anagram.
                }
            }
        }

        return result;
    }

    /**
     * Main method for testing the AnagramSearch class.
     */
    public static void main(String[] args) {
        AnagramSearch anagramSearch = new AnagramSearch();

        // Test case 1
        String text1 = "FEGHKEFGHE";
        String pattern1 = "EFGH";
        List<Integer> result1 = anagramSearch.findAnagrams(text1, pattern1);
        System.out.println("Test Case 1: Text = " + text1 + ", Pattern = " + pattern1 + ", Indices = " + result1);
        // Expected output: [0, 5, 6]

        // Test case 2 (Edge case: empty text)
        String text2 = "";
        String pattern2 = "EFGH";
        List<Integer> result2 = anagramSearch.findAnagrams(text2, pattern2);
        System.out.println("Test Case 2: Text = " + text2 + ", Pattern = " + pattern2 + ", Indices = " + result2);
        // Expected output: []

        // Test case 3 (Edge case: empty pattern)
        String text3 = "FEGHKEFGHE";
