        List<Integer> duplicates6 = duplicateFinder.findDuplicates(arr6);
        System.out.println("Test Case 6: " + duplicates6); // Expected: []

        // Test Case 7: Large array with some duplicates
        int[] arr7 = new int[1000];
        for (int i = 0; i < 1000; i++) {
